// src/modules/ScientistHomeModule.jsx
import React, { useEffect, useState, useMemo, useRef } from "react";
import axios from "axios";
import "./ScientistHomeModule.css";

// Backend URL - update this to your FastAPI endpoint
const BACKEND_URL = "http://10.121.243.94:8000/visualize/map";

// Available parameters (exact list you requested)
const PARAMETERS = [
  { value: "DIC", label: "DIC" },
  { value: "MLD", label: "MLD" },
  { value: "PCO2_ORIGINAL", label: "PCO2_ORIGINAL" },
  { value: "CHL", label: "CHL" },
  { value: "NO3", label: "NO3" },
  { value: "SSS", label: "SSS" },
  { value: "SST", label: "SST" },
  { value: "DEVIANT_UNCERTAINTY", label: "DEVIANT_UNCERTAINTY" },
];

const ScientistHomeModule = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLive, setIsLive] = useState(false);

  // default to DEVIANT_UNCERTAINTY so the example URL is immediate
  const [parameter, setParameter] = useState("DEVIANT_UNCERTAINTY");

  const [plotImage, setPlotImage] = useState(null);
  const mountedRef = useRef(true);
  const controllerRef = useRef(null);

  // Generate plot from FastAPI backend (image response)
  const generatePlot = async () => {
    setError(null);
    setLoading(true);
    setIsLive(false);

    // Clean up previous image
    if (plotImage) {
      try {
        URL.revokeObjectURL(plotImage);
      } catch (e) {}
      setPlotImage(null);
    }

    // Cancel any ongoing request
    if (controllerRef.current) {
      try {
        controllerRef.current.abort();
      } catch (e) {}
    }

    const controller = new AbortController();
    controllerRef.current = controller;

    try {
      const response = await axios.get(BACKEND_URL, {
        params: { parameter }, // <-- sends ?parameter=DEVIANT_UNCERTAINTY
        responseType: "blob",
        timeout: 30000,
        signal: controller.signal,
        headers: {
          Accept: "image/png, image/jpeg, application/octet-stream",
        },
      });

      if (!mountedRef.current) return;

      const blob = response?.data;
      if (!blob) {
        throw new Error("No image data received from server.");
      }

      const url = URL.createObjectURL(blob);
      setPlotImage(url);
      setIsLive(true);
      setError(null);
    } catch (err) {
      if (!mountedRef.current) return;
      if (axios.isCancel?.(err)) {
        console.log("Request cancelled");
      } else {
        console.error("Failed to generate plot:", err);
        if (err.response) {
          setError(
            `Server returned ${err.response.status}: ${err.response.statusText}`
          );
        } else if (err.request) {
          setError(
            "No response from server. Confirm backend is running and accessible."
          );
        } else {
          setError(err.message || "Unknown error");
        }
      }
      setIsLive(false);
    } finally {
      if (mountedRef.current) setLoading(false);
    }
  };

  // Open the map visualization in a new tab using the selected parameter
  const openMapInNewTab = () => {
    const url = `${BACKEND_URL}?parameter=${encodeURIComponent(parameter)}`;
    window.open(url, "_blank");
  };

  // Cleanup on unmount
  useEffect(() => {
    mountedRef.current = true;

    return () => {
      mountedRef.current = false;
      if (controllerRef.current) {
        try {
          controllerRef.current.abort();
        } catch (e) {}
      }
      if (plotImage) {
        try {
          URL.revokeObjectURL(plotImage);
        } catch (e) {}
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // (If you have a time-series 'series' variable used elsewhere, keep it as before.
  //  For brevity I leave other UI logic unchanged — only the parameter handling and extra button are added.)

  return (
    <div className="scientist-home">
      {/* HEADER */}
      <header className="scientist-home-header">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "12px" }}>
          <h3>Real-time Oceanography Snapshot</h3>
          <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
            {/* Label must read "Choose Parameter:" */}
            <label htmlFor="parameter-select" style={{ fontSize: "0.9rem", color: "#9ca3af" }}>
              Choose Parameter:
            </label>

            <select
              id="parameter-select"
              value={parameter}
              onChange={(e) => setParameter(e.target.value)}
              style={{
                padding: "6px 12px",
                borderRadius: "8px",
                border: "1px solid rgba(148, 163, 184, 0.3)",
                background: "rgba(15, 23, 42, 0.85)",
                color: "#e5e7eb",
                fontSize: "0.9rem",
                cursor: "pointer",
              }}
            >
              {PARAMETERS.map((param) => (
                <option key={param.value} value={param.value}>
                  {param.label}
                </option>
              ))}
            </select>

            <button
              onClick={generatePlot}
              disabled={loading}
              style={{
                padding: "8px 20px",
                borderRadius: "999px",
                border: "none",
                background: loading ? "rgba(148, 163, 184, 0.3)" : "linear-gradient(135deg, #2dd4bf, #38bdf8)",
                color: loading ? "#9ca3af" : "#020617",
                fontSize: "0.9rem",
                fontWeight: "600",
                cursor: loading ? "not-allowed" : "pointer",
                boxShadow: loading ? "none" : "0 0 14px rgba(45, 212, 191, 0.5)",
                transition: "all 0.2s ease",
              }}
            >
              {loading ? "Generating..." : "Generate Plot"}
            </button>

            {/* New button to open the map visualization endpoint directly */}
            <button
              onClick={openMapInNewTab}
              style={{
                padding: "8px 16px",
                borderRadius: "8px",
                border: "1px solid rgba(148,163,184,0.2)",
                background: "#ffffff00",
                color: "#e5e7eb",
                fontSize: "0.9rem",
                cursor: "pointer",
                marginLeft: "8px",
              }}
              title={`Open ${BACKEND_URL}?parameter=${parameter}`}
            >
              Open Map
            </button>
          </div>
        </div>
        <p>
          Select a parameter and click "Generate Plot" to visualize {parameter} data for the Indian EEZ.
        </p>
      </header>

      {/* The rest of your component (image display, metrics, etc.) remains unchanged */}
      {/* ... */}
    </div>
  );
};

export default ScientistHomeModule;
