// VisualizationTool.js
import React, { useState, useEffect, useCallback, useMemo, useRef } from "react";
import "./VisualizationTool.css";
import VisualizationParametersPanel from "./VisualizationParametersPanel";
import axios from "axios";

const VisualizationTool = () => {
  // existing state
  const [timeRange, setTimeRange] = useState("last7");
  const [time1, setTime1] = useState("");
  const [time2, setTime2] = useState("");
  const [vizParams, setVizParams] = useState({
    independentVariable: "Time",
    parameters: ["SST"],
  });
  const [plotType, setPlotType] = useState("line");
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const controllerRef = useRef(null);
  const mountedRef = useRef(true);

  // NEW: domain state (Oceanography | Biodiversity | Taxonomy)
  const [domain, setDomain] = useState("Oceanography");
  // When domain === "Taxonomy" the taxonomyEndpoint holds which endpoint the user picked
  const [taxonomyEndpoint, setTaxonomyEndpoint] = useState(""); // one of: list, species, filter, map
  // For species/{name} we show an input
  const [taxonomySpeciesName, setTaxonomySpeciesName] = useState("");

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
      if (controllerRef.current) {
        try { controllerRef.current.abort(); } catch (e) {}
      }
      if (imageUrl) {
        try { URL.revokeObjectURL(imageUrl); } catch (e) {}
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const toDDMMYYYY = useCallback((d) => {
    const dt = new Date(d);
    const yyyy = dt.getFullYear();
    const mm = String(dt.getMonth() + 1).padStart(2, "0");
    const dd = String(dt.getDate()).padStart(2, "0");
    return `${dd}/${mm}/${yyyy}`;
  }, []);

  const setLastNDays = useCallback((n) => {
    const now = new Date();
    const endStr = toDDMMYYYY(now);
    const start = new Date(now);
    start.setDate(start.getDate() - n);
    const startStr = toDDMMYYYY(start);
    setTime1(startStr);
    setTime2(endStr);
  }, [toDDMMYYYY]);

  useEffect(() => {
    if (timeRange === "last7") setLastNDays(7);
    else if (timeRange === "last30") setLastNDays(30);
    else if (timeRange === "custom") {
      if (!time1) setTime1(toDDMMYYYY(new Date()));
      if (!time2) setTime2(toDDMMYYYY(new Date()));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [timeRange]);

  useEffect(() => {
    setLastNDays(7);
  }, [setLastNDays]);

  const toYYYYMMDD = useCallback((ddmmyyyy) => {
    if (!ddmmyyyy) return "";
    const parts = ddmmyyyy.split("/");
    if (parts.length !== 3) return ddmmyyyy;
    const [dd, mm, yyyy] = parts;
    return `${yyyy}-${mm}-${dd}`;
  }, []);

  const fieldMap = useMemo(() => ({
    Time: "datetime",
    Latitude: "lat",
    Longitude: "lon",
    DIC: "dic",
    MLD: "mld",
    PCO2_ORIGINAL: "pco2_original",
    CHL: "chl",
    NO3: "no3",
    SSS: "sss",
    SST: "sst",
    DEVIANT_UNCERTAINTY: "deviant_uncertainty",
  }), []);

  const xOptions = useMemo(() => ["Latitude", "Longitude", "Time"], []);
  const yOptions = useMemo(() => [
    "DIC","MLD","PCO2_ORIGINAL","CHL","NO3","SSS","SST","DEVIANT_UNCERTAINTY"
  ], []);
  const initialY = useMemo(() => ["SST"], []);

  const handleVizParamsChange = useCallback((payload) => {
    setVizParams((prev) => {
      try {
        if (JSON.stringify(prev) === JSON.stringify(payload)) return prev;
      } catch (e) {}
      return payload;
    });
  }, []);

  const buildPayloadAndParams = useCallback(() => {
    const parameters = vizParams?.parameters || [];
    const start_date = toYYYYMMDD(time1);
    const end_date = toYYYYMMDD(time2);
    const x_axis = fieldMap[vizParams.independentVariable] || vizParams.independentVariable;
    const y_axes = parameters.map((p) => fieldMap[p] || p);

    // Default ocean/biodiversity payload
    const body = {
      start_date,
      end_date,
      time_range: timeRange,
      x_axis,
      y_axes,
      x_label: vizParams.independentVariable,
      y_labels: parameters,
      plot_type: plotType,
      domain,
    };

    const params = {
      plot_type: plotType,
      x: x_axis,
      y: y_axes,
      start_date,
      end_date,
      domain,
    };

    // If taxonomy domain, include taxonomy meta
    if (domain === "Taxonomy") {
      body.taxonomy_endpoint = taxonomyEndpoint || undefined;
      body.species_name = taxonomySpeciesName || undefined;
      params.taxonomy_endpoint = taxonomyEndpoint || undefined;
      if (taxonomySpeciesName) params.species_name = taxonomySpeciesName;
    }

    return { body, params };
  }, [vizParams, time1, time2, timeRange, fieldMap, toYYYYMMDD, plotType, domain, taxonomyEndpoint, taxonomySpeciesName]);

  const handleApply = useCallback(async () => {
    setError(null);

    // If taxonomy domain we don't require selected Y parameters necessarily,
    // because taxonomy endpoints are separate. But for oceanography/bio keep checks.
    if (domain !== "Taxonomy") {
      const parameters = vizParams?.parameters || [];
      if (!parameters || parameters.length === 0) {
        setError(new Error("Select at least one Y parameter."));
        return;
      }
    } else {
      // If taxonomy -> check endpoint selection
      if (!taxonomyEndpoint) {
        setError(new Error("Select a taxonomy action (List / Species / Filter / Map)."));
        return;
      }
      if (taxonomyEndpoint === "species" && !taxonomySpeciesName) {
        setError(new Error("Enter species name for Species Info endpoint."));
        return;
      }
    }

    if (imageUrl) {
      try { URL.revokeObjectURL(imageUrl); } catch (e) {}
      setImageUrl(null);
    }

    setLoading(true);

    // Backend base: use different base when taxonomy requested (you can change these URLs)
    const baseOceanBackend = "http://192.168.0.3:8000/ocean-overlay/multi";
    const baseTaxonomyBackend = "http://192.168.0.3:8000/taxonomy"; // server should expose taxonomy endpoints

    const { body, params } = buildPayloadAndParams();

    if (controllerRef.current) {
      try { controllerRef.current.abort(); } catch (e) {}
    }
    const controller = new AbortController();
    controllerRef.current = controller;

    try {
      const postConfig = {
        responseType: "blob",
        timeout: 45000,
        signal: controller.signal,
        headers: {
          "Content-Type": "application/json",
          Accept: "image/png, image/jpeg, application/octet-stream",
        },
      };

      let resp;
      if (domain === "Taxonomy") {
        // If taxonomy, call the specific taxonomy endpoint.
        // Map taxonomyEndpoint to server path:
        // list -> /taxonomy/list
        // species -> /taxonomy/species/{name}
        // filter -> /taxonomy/filter
        // map -> /taxonomy/map
        let taxonomyPath = "/list";
        if (taxonomyEndpoint === "list") taxonomyPath = "/list";
        else if (taxonomyEndpoint === "species") {
          // species name is included in path
          const encodedName = encodeURIComponent(taxonomySpeciesName || "");
          taxonomyPath = `/species/${encodedName}`;
        } else if (taxonomyEndpoint === "filter") taxonomyPath = "/filter";
        else if (taxonomyEndpoint === "map") taxonomyPath = "/map";

        const taxonomyUrl = `${baseTaxonomyBackend}${taxonomyPath}`;

        // Many taxonomy endpoints will be GET-only. Try GET first with params.
        try {
          // Build query string (if any)
          const qp = [];
          if (params.start_date) qp.push(`start_date=${encodeURIComponent(params.start_date)}`);
          if (params.end_date) qp.push(`end_date=${encodeURIComponent(params.end_date)}`);
          if (params.domain) qp.push(`domain=${encodeURIComponent(params.domain)}`);
          const qstr = qp.length ? `?${qp.join("&")}` : "";
          resp = await axios.get(`${taxonomyUrl}${qstr}`, {
            responseType: "blob",
            timeout: 45000,
            signal: controller.signal,
            headers: { Accept: "image/png, image/jpeg, application/octet-stream, application/json" },
          });
        } catch (errGet) {
          // If GET fails (or server expects POST), try POST
          resp = await axios.post(taxonomyUrl, body, postConfig);
        }
      } else {
        // Non-taxonomy (oceanography / biodiversity) - keep existing behavior: POST, fallback to GET on 405
        try {
          resp = await axios.post(baseOceanBackend, body, postConfig);
        } catch (postErr) {
          if (postErr?.response?.status === 405) {
            // fallback GET: build query string with y repeated
            const queryParts = [];
            queryParts.push(`plot_type=${encodeURIComponent(params.plot_type)}`);
            queryParts.push(`x=${encodeURIComponent(params.x)}`);
            if (Array.isArray(params.y)) {
              params.y.forEach(yVal => queryParts.push(`y=${encodeURIComponent(yVal)}`));
            }
            if (params.start_date) queryParts.push(`start_date=${encodeURIComponent(params.start_date)}`);
            if (params.end_date) queryParts.push(`end_date=${encodeURIComponent(params.end_date)}`);
            if (params.domain) queryParts.push(`domain=${encodeURIComponent(params.domain)}`);
            const queryString = queryParts.join("&");
            const urlWithQuery = `${baseOceanBackend}?${queryString}`;
            resp = await axios.get(urlWithQuery, {
              responseType: "blob",
              timeout: 45000,
              signal: controller.signal,
              headers: { Accept: "image/png, image/jpeg, application/octet-stream" },
            });
          } else {
            throw postErr;
          }
        }
      }

      if (!mountedRef.current) return;

      const blob = resp?.data;
      if (!blob) throw new Error("No binary data received from server.");

      const url = URL.createObjectURL(blob);
      setImageUrl(url);
      setError(null);
    } catch (err) {
      if (!mountedRef.current) return;
      if (axios.isCancel?.(err)) {
        console.log("Request cancelled");
      } else {
        console.error("Failed to fetch image:", err);
        if (err.response) setError(new Error(`Server returned ${err.response.status}: ${err.response.statusText}`));
        else if (err.request) setError(new Error("No response from server. Check backend & CORS."));
        else setError(new Error(err.message || "Unknown error"));
      }
    } finally {
      if (mountedRef.current) setLoading(false);
    }
  }, [vizParams, imageUrl, buildPayloadAndParams, domain, taxonomyEndpoint, taxonomySpeciesName]);

  // taxonomy accordion items (for UI rendering)
  const taxonomyItems = useMemo(() => ([
    { key: "list", label: "/taxonomy/list", desc: "List Species" },
    { key: "species", label: "/taxonomy/species/{name}", desc: "Species Info" },
    { key: "filter", label: "/taxonomy/filter", desc: "Filter Taxonomy" },
    { key: "map", label: "/taxonomy/map", desc: "Taxonomy Species Map" },
  ]), []);

  return (
    <div className="viz-layout reverse-layout">
      {/* RIGHT: FILTERS */}
      <section className="viz-panel viz-panel-left">
        <h3 className="viz-panel-title">Data & Filters</h3>
        <p className="viz-panel-text">Select domain, region, time window and plot type to request a plot image from the backend.</p>

        {/* DOMAIN DROPDOWN - now includes Taxonomy */}
        <div className="viz-form-group">
          <label>Domain</label>
          <select value={domain} onChange={(e) => {
            const v = e.target.value;
            setDomain(v);
            // clear taxonomy selections if switching away
            if (v !== "Taxonomy") {
              setTaxonomyEndpoint("");
              setTaxonomySpeciesName("");
            }
          }}>
            <option>Oceanography</option>
            <option>Biodiversity</option>
            <option>Taxonomy</option>
          </select>
        </div>

        {/* When Taxonomy domain chosen, render taxonomy accordion UI */}
        {domain === "Taxonomy" && (
          <div className="viz-form-group taxonomy-accordion-root">
            <label>Taxonomy</label>
            <div className="taxonomy-accordion">
              {taxonomyItems.map((it) => (
                <div key={it.key} className={`taxonomy-row ${taxonomyEndpoint === it.key ? "taxonomy-selected" : ""}`}>
                  <div className="taxonomy-left">
                    <span className="taxonomy-get">GET</span>
                    <button
                      type="button"
                      className="taxonomy-endpoint-btn"
                      onClick={() => {
                        setTaxonomyEndpoint(it.key);
                        // if the item is species, keep species input visible
                        if (it.key !== "species") setTaxonomySpeciesName("");
                      }}
                    >
                      {it.label}
                    </button>
                  </div>
                  <div className="taxonomy-right">
                    <small className="taxonomy-desc">{it.desc}</small>
                    <div className="taxonomy-actions">
                      {/* For species endpoint show species name input */}
                      {it.key === "species" && taxonomyEndpoint === "species" && (
                        <input
                          className="taxonomy-species-input"
                          placeholder="Enter species name (e.g. Thunnus albacares)"
                          value={taxonomySpeciesName}
                          onChange={(e) => setTaxonomySpeciesName(e.target.value)}
                        />
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
            <small className="viz-hint">Select a taxonomy endpoint — chosen endpoint will be sent to the server with the request.</small>
          </div>
        )}

        {/* existing region/time & param controls (unchanged) */}
        <div className="viz-form-group">
          <label>Region</label>
          <select>
            <option>Entire Indian EEZ</option>
            <option>Arabian Sea</option>
            <option>Bay of Bengal</option>
            <option>Andaman Sea</option>
          </select>
        </div>

        <div className="viz-form-group">
          <label>Time Range</label>
          <select value={timeRange} onChange={(e) => setTimeRange(e.target.value)}>
            <option value="last7">Last 7 days</option>
            <option value="last30">Last 30 days</option>
            <option value="custom">Custom</option>
          </select>
        </div>

        {timeRange === "custom" && (
          <>
            <div className="viz-form-group">
              <label>Start date</label>
              <input type="date" value={toYYYYMMDD(time1)} onChange={(e) => {
                const yyyymmdd = e.target.value;
                if (yyyymmdd) {
                  const [yyyy, mm, dd] = yyyymmdd.split("-");
                  setTime1(`${dd}/${mm}/${yyyy}`);
                } else setTime1("");
              }} className="date-input" />
            </div>

            <div className="viz-form-group">
              <label>End date</label>
              <input type="date" value={toYYYYMMDD(time2)} onChange={(e) => {
                const yyyymmdd = e.target.value;
                if (yyyymmdd) {
                  const [yyyy, mm, dd] = yyyymmdd.split("-");
                  setTime2(`${dd}/${mm}/${yyyy}`);
                } else setTime2("");
              }} className="date-input" />
            </div>
          </>
        )}

        <div style={{ marginTop: 18 }}>
          <VisualizationParametersPanel xOptions={xOptions} yOptions={yOptions} initialX="Time" initialY={initialY} onChange={handleVizParamsChange} />
        </div>

        <button type="button" className="viz-apply-btn" onClick={handleApply} disabled={loading}>{loading ? "Generating image…" : "Apply Filters"}</button>

        {error && <div className="viz-error" style={{ marginTop: 12, color: "#ffbdbd" }}>{error.message || "Failed to generate image. See console."}</div>}
      </section>

      {/* LEFT: image/map view (unchanged) */}
      <section className="viz-panel viz-panel-main">
        <div className="viz-panel-header">
          <div>
            <h3 className="viz-panel-title">Map / Plot View</h3>
            <p className="viz-panel-text">The backend will return a plot image (PNG/JPEG). It will be displayed here when ready.</p>
          </div>
        </div>

        <div className="viz-map-placeholder" style={{ display: "flex", alignItems: "center", justifyContent: "center", minHeight: 360 }}>
          {imageUrl ? (
            <img src={imageUrl} alt="Generated plot" style={{ maxWidth: "100%", maxHeight: "640px", objectFit: "contain", borderRadius: 8, boxShadow: "0 6px 20px rgba(2,6,23,0.6)" }} />
          ) : loading ? (
            <div style={{ color: "#9fb4d8" }}>Generating image…</div>
          ) : (
            <div style={{ textAlign: "center", color: "#9fb4d8" }}>
              <p style={{ marginBottom: 6 }}>Spatial visualization / plot will appear here</p>
              <small>Click "Apply Filters" to request a plot image from the backend.</small>
            </div>
          )}
        </div>
      </section>

      {/* BOTTOM metrics unchanged */}
      <section className="viz-panel viz-panel-bottom">
        <h3 className="viz-panel-title">Time Series & Indicators</h3>
        <p className="viz-panel-text">Summary metrics (static placeholders)</p>

        <div className="viz-metrics-grid">
          <div className="viz-metric-card">
            <p className="viz-metric-label">SST (°C)</p>
            <p className="viz-metric-value">27.3</p>
            <p className="viz-metric-note">Near long-term average.</p>
          </div>
          <div className="viz-metric-card">
            <p className="viz-metric-label">Chl-a (mg/m³)</p>
            <p className="viz-metric-value">0.8</p>
            <p className="viz-metric-note">Elevated coastal bloom.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default VisualizationTool;
