// DataInsightsModule.js
import React, { useState, useEffect, useCallback, useRef } from "react";
import "./DataInsightsModule.css";
import axios from "axios";

/*
  This module now hosts the Taxonomy UI previously in VisualizationTool:
  - Select: List Species | Species Taxonomy | Filter Taxonomy
  - Inputs for each
  - Sends GET (preferred) with query params or POST fallback
  - Shows pretty JSON + table/cards summary for beginner-friendly viewing
*/

function formatDate(iso) {
  try {
    const d = new Date(iso);
    return d.toLocaleString(undefined, {
      year: "numeric",
      month: "short",
      day: "2-digit",
      hour: "2-digit",
      minute: "2-digit",
      hour12: false,
      timeZoneName: "short",
    });
  } catch (e) {
    return iso;
  }
}

function shortId(id) {
  if (!id) return "";
  return id.length > 10 ? `${id.slice(0, 8)}…` : id;
}

/* A compact beginner-friendly renderer for taxonomy JSON */
function TaxonomyResultView({ data }) {
  if (!data) return null;

  const items = data.data || [];
  const count = data.count ?? items.length;

  // Build a simple list of species names
  const speciesNames = items.map((r) => r.scientific_name || r.species).filter(Boolean);

  return (
    <div style={{ width: "100%" }}>
      {/* Summary */}
      <div style={{
        display: "flex",
        justifyContent: "space-between",
        gap: 12,
        alignItems: "center",
        marginBottom: 12
      }}>
        <div>
          <h4 style={{ margin: 0, color: "#e6eef6" }}>Found {count} record{count !== 1 ? "s" : ""}</h4>
          <div style={{ color: "#9fb4d8", fontSize: 13, marginTop: 6 }}>
            Species (first {Math.min(5, speciesNames.length)}): {speciesNames.slice(0, 5).join(", ") || "—"}
            {speciesNames.length > 5 ? ` + ${speciesNames.length - 5} more` : ""}
          </div>
        </div>

        <div style={{ display: "flex", gap: 8 }}>
          <button
            className="ins-action-btn"
            onClick={() => {
              try { navigator.clipboard.writeText(JSON.stringify(data, null, 2)); }
              catch (e) { console.warn(e); }
            }}
          >Copy JSON</button>

          <a
            className="ins-action-btn"
            style={{ textDecoration: "none" }}
            href={`data:application/json;charset=utf-8,${encodeURIComponent(JSON.stringify(data, null, 2))}`}
            download="taxonomy_response_pretty.json"
          >Download</a>
        </div>
      </div>

      {/* Table (good for scanning) */}
      <div className="tax-table-wrap">
        <table className="tax-table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Scientific name</th>
              <th>Family</th>
              <th>Genus</th>
              <th>Locality</th>
              <th>Lat,Lon</th>
              <th>Created</th>
            </tr>
          </thead>
          <tbody>
            {items.map((it) => (
              <tr key={it.id || `${it.scientific_name}-${Math.random()}`}>
                <td title={it.id}>{shortId(it.id)}</td>
                <td>{it.scientific_name || it.species || "—"}</td>
                <td>{it.family || "—"}</td>
                <td>{it.genus || "—"}</td>
                <td>{it.locality || "—"}</td>
                <td>
                  {it.lat != null && it.lon != null ? (
                    <a
                      href={`https://www.openstreetmap.org/?mlat=${it.lat}&mlon=${it.lon}#map=12/${it.lat}/${it.lon}`}
                      target="_blank"
                      rel="noreferrer"
                      style={{ color: "#7dd3fc" }}
                    >
                      {it.lat}, {it.lon}
                    </a>
                  ) : "—"}
                </td>
                <td style={{ whiteSpace: "nowrap" }}>{formatDate(it.created_at)}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Card view — gives details per record and is easy for beginners */}
      <div style={{ marginTop: 14, display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(320px, 1fr))", gap: 12 }}>
        {items.map((it) => (
          <div key={it.id} className="tax-card">
            <div className="tax-card-head">
              <div style={{ fontWeight: 700 }}>{it.scientific_name || it.species || "Unknown species"}</div>
              <div style={{ color: "#94a3b8", fontSize: 12 }}>{it.family || "Family unknown"}</div>
            </div>

            <div className="tax-card-body">
              <div><strong>Genus:</strong> {it.genus || "—"}</div>
              <div><strong>Locality:</strong> {it.locality || "—"}</div>
              <div><strong>Location:</strong> {it.lat != null ? `${it.lat}, ${it.lon}` : "—"}</div>
              <div><strong>Created:</strong> {formatDate(it.created_at)}</div>
            </div>

            <details style={{ marginTop: 8, color: "#cfe7ff" }}>
              <summary style={{ cursor: "pointer" }}>More fields</summary>
              <pre style={{ marginTop: 8, whiteSpace: "pre-wrap", color: "#e6eef6", fontSize: 13 }}>
                {Object.entries(it).map(([k,v]) => `${k}: ${v}\n`).join("")}
              </pre>
            </details>
          </div>
        ))}
      </div>
    </div>
  );
}

const DataInsightsModule = () => {
  const [taxonomySelect, setTaxonomySelect] = useState("");
  const [listCount, setListCount] = useState("");
  const [scientificName, setScientificName] = useState("");
  const [filterFamily, setFilterFamily] = useState("");
  const [filterGenus, setFilterGenus] = useState("");
  const [filterOrder, setFilterOrder] = useState("");

  const [jsonResult, setJsonResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const controllerRef = useRef(null);
  const mountedRef = useRef(true);

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
      if (controllerRef.current) {
        try { controllerRef.current.abort(); } catch (e) {}
      }
    };
  }, []);

  const handleApply = useCallback(async () => {
    setError(null);

    if (!taxonomySelect) {
      setError(new Error("Select a taxonomy action from the dropdown."));
      return;
    }
    if (taxonomySelect === "species" && !scientificName) {
      setError(new Error("Enter Scientific Name for Species Taxonomy."));
      return;
    }
    if (taxonomySelect === "list" && !listCount) {
      setError(new Error("Enter number of species for List Species."));
      return;
    }
    if (taxonomySelect === "filter" && !filterFamily && !filterGenus && !filterOrder) {
      setError(new Error("Enter at least one of Family / Genus / Order for Filter Taxonomy."));
      return;
    }

    if (jsonResult) setJsonResult(null);
    setLoading(true);

    const baseTaxonomyBackend = "http://192.168.0.3:8000/taxonomy";
    if (controllerRef.current) {
      try { controllerRef.current.abort(); } catch (e) {}
    }
    const controller = new AbortController();
    controllerRef.current = controller;

    try {
      let route = "/list";
      if (taxonomySelect === "list") route = "/list";
      else if (taxonomySelect === "species") route = `/species/${encodeURIComponent(scientificName)}`;
      else if (taxonomySelect === "filter") route = "/filter";
      const suffix = route.replace(/^\/taxonomy/, ""); // route already like /list

      const taxonomyUrl = `${baseTaxonomyBackend}${suffix}`;

      // Build query for GET where applicable
      let q = "";
      if (taxonomySelect === "filter") {
        const qp = [];
        if (filterFamily) qp.push(`family=${encodeURIComponent(filterFamily)}`);
        if (filterGenus) qp.push(`genus=${encodeURIComponent(filterGenus)}`);
        if (filterOrder) qp.push(`order=${encodeURIComponent(filterOrder)}`);
        q = qp.length ? `?${qp.join("&")}` : "";
      } else if (taxonomySelect === "list") {
        if (listCount) q = `?limit=${encodeURIComponent(listCount)}&offset=0`;
        else q = `?offset=0`;
      }

      // Try GET expecting JSON
      try {
        const jsonResp = await axios.get(`${taxonomyUrl}${q}`, {
          responseType: "json",
          timeout: 45000,
          signal: controller.signal,
          headers: { Accept: "application/json" },
        });

        if (!mountedRef.current) return;

        if (jsonResp && jsonResp.data) {
          setJsonResult(jsonResp.data);
          setError(null);
          return;
        }
      } catch (getErr) {
        // If GET fails, try POST expecting JSON
        try {
          const postResp = await axios.post(`${baseTaxonomyBackend}${suffix}`, {
            taxonomy_action: taxonomySelect,
            count: listCount,
            scientific_name: scientificName,
            family: filterFamily,
            genus: filterGenus,
            order: filterOrder,
          }, {
            responseType: "json",
            timeout: 45000,
            signal: controller.signal,
            headers: { "Content-Type": "application/json", Accept: "application/json" }
          });

          if (!mountedRef.current) return;

          if (postResp && postResp.data) {
            setJsonResult(postResp.data);
            setError(null);
            return;
          }
        } catch (postErr) {
          // Try blob fallback if server returns an image/file
          try {
            const blobResp = await axios.get(`${taxonomyUrl}${q}`, {
              responseType: "blob",
              timeout: 45000,
              signal: controller.signal,
            });
            if (!mountedRef.current) return;
            // Not handling blob display inside DataInsights (we prefer JSON); throw
            throw new Error("Server returned binary data; expected JSON.");
          } catch (finalErr) {
            throw postErr || getErr || finalErr;
          }
        }
      }

      throw new Error("No valid response from taxonomy server.");
    } catch (err) {
      if (!mountedRef.current) return;
      console.error("Taxonomy request failed:", err);
      if (axios.isCancel?.(err)) {
        // noop
      } else {
        if (err.response) setError(new Error(`Server returned ${err.response.status}: ${err.response.statusText}`));
        else if (err.request) setError(new Error("No response from server. Check backend & CORS."));
        else setError(new Error(err.message || "Unknown error"));
      }
    } finally {
      if (mountedRef.current) setLoading(false);
    }
  }, [taxonomySelect, listCount, scientificName, filterFamily, filterGenus, filterOrder, jsonResult]);

  return (
    <div className="ins-layout">
      <div className="ins-banner">
        <span className="ins-banner-label">DATA INSIGHTS · TAXONOMY</span>
        <span className="ins-banner-sub">Taxonomy endpoints and results viewer moved here.</span>
      </div>

      <section className="ins-grid">
        {/* LEFT: controls */}
        <div className="ins-panel ins-left">
          <h3 className="ins-title">Taxonomy</h3>
          <p className="ins-text">Select a taxonomy action and provide required inputs, then click Apply.</p>

          <div className="ins-field">
            <label>Select action</label>
            <select value={taxonomySelect} onChange={(e) => {
              setTaxonomySelect(e.target.value);
              setJsonResult(null);
              setError(null);
              setListCount("");
              setScientificName("");
              setFilterFamily("");
              setFilterGenus("");
              setFilterOrder("");
            }}>
              <option value="">-- choose action --</option>
              <option value="list">List Species</option>
              <option value="species">Species Taxonomy</option>
              <option value="filter">Filter Taxonomy</option>
            </select>
          </div>

          {taxonomySelect === "list" && (
            <div className="ins-field">
              <label>Enter number of species</label>
              <input placeholder="Enter number of species" value={listCount} onChange={(e) => setListCount(e.target.value)} />
            </div>
          )}

          {taxonomySelect === "species" && (
            <div className="ins-field">
              <label>Enter Scientific Name</label>
              <input placeholder="Enter Scientific Name" value={scientificName} onChange={(e) => setScientificName(e.target.value)} />
            </div>
          )}

          {taxonomySelect === "filter" && (
            <>
              <div className="ins-field">
                <label>Family</label>
                <input placeholder="Enter Family" value={filterFamily} onChange={(e) => setFilterFamily(e.target.value)} />
              </div>
              <div className="ins-field">
                <label>Genus</label>
                <input placeholder="Enter Genus" value={filterGenus} onChange={(e) => setFilterGenus(e.target.value)} />
              </div>
              <div className="ins-field">
                <label>Order</label>
                <input placeholder="Enter Order" value={filterOrder} onChange={(e) => setFilterOrder(e.target.value)} />
              </div>
            </>
          )}

          <div style={{ marginTop: 10 }}>
            <button className="ins-action-btn" onClick={handleApply} disabled={loading}>{loading ? "Fetching…" : "Apply"}</button>
          </div>

          {error && <div style={{ marginTop: 10, color: "#ffbdbd" }}>{error.message}</div>}
        </div>

        {/* RIGHT: results */}
        <div className="ins-panel ins-right">
          <h3 className="ins-title">Results</h3>
          <p className="ins-text">Taxonomy response will appear here in a beginner-friendly format.</p>

          {jsonResult ? (
            <TaxonomyResultView data={jsonResult} />
          ) : loading ? (
            <div style={{ color: "#9fb4d8" }}>Fetching taxonomy data…</div>
          ) : (
            <div style={{ color: "#9ca3af" }}>
              <p>No taxonomy response yet.</p>
              <p>Choose an action and click Apply to fetch results.</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default DataInsightsModule;
