// src/modules/ScientistHomeModule.jsx
import React, { useEffect, useState, useMemo, useRef } from "react";
import axios from "axios";
import "./ScientistHomeModule.css";

// Backend URL - update this to your FastAPI endpoint
const BACKEND_URL = "http://10.121.243.94:8000/visualize/map";

// Available parameters
const PARAMETERS = [
  { value: "DIC", label: "DIC" },
  { value: "MLD", label: "MLD" },
  { value: "PCO2_ORIGINAL", label: "PCO2_ORIGINAL" },
  { value: "CHL", label: "CHL" },
  { value: "NO3", label: "NO3" },
  { value: "SSS", label: "SSS" },
  { value: "SST", label: "SST" },
  { value: "DEVIANT_UNCERTAINTY", label: "DEVIANT_UNCERTAINTY" },
];

const ScientistHomeModule = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLive, setIsLive] = useState(false);
  const [parameter, setParameter] = useState("SST");
  const [plotImage, setPlotImage] = useState(null);
  const mountedRef = useRef(true);
  const controllerRef = useRef(null);

  // Generate plot from FastAPI backend
  const generatePlot = async () => {
    setError(null);
    setLoading(true);
    setIsLive(false);

    // Clean up previous image
    if (plotImage) {
      try {
        URL.revokeObjectURL(plotImage);
      } catch (e) {}
      setPlotImage(null);
    }

    // Cancel any ongoing request
    if (controllerRef.current) {
      try {
        controllerRef.current.abort();
      } catch (e) {}
    }

    const controller = new AbortController();
    controllerRef.current = controller;

    try {
      const response = await axios.get(BACKEND_URL, {
        params: { parameter },
        responseType: "blob",
        timeout: 30000,
        signal: controller.signal,
        headers: {
          Accept: "image/png, image/jpeg, application/octet-stream",
        },
      });

      if (!mountedRef.current) return;

      const blob = response?.data;
      if (!blob) {
        throw new Error("No image data received from server.");
      }

      const url = URL.createObjectURL(blob);
      setPlotImage(url);
      setIsLive(true);
      setError(null);
    } catch (err) {
      if (!mountedRef.current) return;
      if (axios.isCancel?.(err)) {
        console.log("Request cancelled");
      } else {
        console.error("Failed to generate plot:", err);
        if (err.response) {
          setError(
            `Server returned ${err.response.status}: ${err.response.statusText}`
          );
        } else if (err.request) {
          setError(
            "No response from server. Confirm backend is running and accessible."
          );
        } else {
          setError(err.message || "Unknown error");
        }
      }
      setIsLive(false);
    } finally {
      if (mountedRef.current) setLoading(false);
    }
  };

  // Cleanup on unmount
  useEffect(() => {
    mountedRef.current = true;

    return () => {
      mountedRef.current = false;
      if (controllerRef.current) {
        try {
          controllerRef.current.abort();
        } catch (e) {}
      }
      if (plotImage) {
        try {
          URL.revokeObjectURL(plotImage);
        } catch (e) {}
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div className="scientist-home">
      {/* HEADER */}
      <header className="scientist-home-header">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "12px" }}>
          <h3>Real-time Oceanography Snapshot</h3>
          <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
            <label htmlFor="parameter-select" style={{ fontSize: "0.9rem", color: "#9ca3af" }}>
              Choose Parameter:
            </label>
            <select
              id="parameter-select"
              value={parameter}
              onChange={(e) => setParameter(e.target.value)}
              style={{
                padding: "6px 12px",
                borderRadius: "8px",
                border: "1px solid rgba(148, 163, 184, 0.3)",
                background: "rgba(15, 23, 42, 0.85)",
                color: "#e5e7eb",
                fontSize: "0.9rem",
                cursor: "pointer",
              }}
            >
              {PARAMETERS.map((param) => (
                <option key={param.value} value={param.value}>
                  {param.label}
                </option>
              ))}
            </select>
            <button
              onClick={generatePlot}
              disabled={loading}
              style={{
                padding: "8px 20px",
                borderRadius: "999px",
                border: "none",
                background: loading ? "rgba(148, 163, 184, 0.3)" : "linear-gradient(135deg, #2dd4bf, #38bdf8)",
                color: loading ? "#9ca3af" : "#020617",
                fontSize: "0.9rem",
                fontWeight: "600",
                cursor: loading ? "not-allowed" : "pointer",
                boxShadow: loading ? "none" : "0 0 14px rgba(45, 212, 191, 0.5)",
                transition: "all 0.2s ease",
              }}
            >
              {loading ? "Generating..." : "Generate Plot"}
            </button>
          </div>
        </div>
        <p>
          Select a parameter and click "Generate Plot" to visualize {parameter} data for the Indian EEZ.
        </p>
      </header>

      {/* REAL-TIME PLOT */}
      <section className="realtime-plot-container">
        <div className="realtime-plot-inner">
          <div className="realtime-plot-header-row">
            <div>
              <p className="realtime-plot-title">
                {parameter} · Real-time Stream
              </p>
              <p className="realtime-plot-sub">
                {error ? `Error: ${error}` : 'Real-time data from FastAPI backend'}
              </p>
            </div>
            <span className={`realtime-badge ${isLive ? 'live' : 'offline'}`}>
              {loading ? 'Connecting...' : isLive ? `Live · ${series[series.length - 1]?.value.toFixed(2)}°C` : 'Offline'}
            </span>
          </div>

          {/* Plot Image Display */}
          <div style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "400px",
            padding: "20px",
          }}>
            {plotImage ? (
              <img
                src={plotImage}
                alt={`${parameter} visualization plot`}
                style={{
                  maxWidth: "100%",
                  maxHeight: "600px",
                  objectFit: "contain",
                  borderRadius: "8px",
                  boxShadow: "0 6px 20px rgba(2,6,23,0.6)",
                }}
              />
            ) : loading ? (
              <div style={{ color: "#9fb4d8", textAlign: "center" }}>
                <p style={{ fontSize: "1.1rem", marginBottom: "8px" }}>Generating plot...</p>
                <p style={{ fontSize: "0.9rem", opacity: 0.7 }}>Please wait while the backend processes your request</p>
              </div>
            ) : error ? (
              <div style={{ color: "#fca5a5", textAlign: "center" }}>
                <p style={{ fontSize: "1.1rem", marginBottom: "8px" }}>Error generating plot</p>
                <p style={{ fontSize: "0.9rem" }}>{error}</p>
              </div>
            ) : (
              <div style={{ textAlign: "center", color: "#9fb4d8" }}>
                <p style={{ fontSize: "1.1rem", marginBottom: "8px" }}>No plot generated yet</p>
                <p style={{ fontSize: "0.9rem", opacity: 0.7 }}>
                  Select a parameter and click "Generate Plot" to visualize the data
                </p>
              </div>
            )}
          </div>
        </div>
      </section>

      {/* METRICS + ACTIONS ROW */}
      <section className="home-metrics-grid">
        <div className="home-metric-card">
          <p className="home-metric-label">Current {parameter}</p>
          <p className="home-metric-value">
            {series[series.length - 1]?.value.toFixed(2)}
          </p>
          <p className="home-metric-note">
            Latest value from real-time stream.
          </p>
        </div>

        <div className="home-metric-card">
          <p className="home-metric-label">Last Δ{parameter}</p>
          <p className="home-metric-value">
            {(
              series[series.length - 1].value -
              series[series.length - 3].value
            ).toFixed(2)}
          </p>
          <p className="home-metric-note">
            Change over last two samples in the stream.
          </p>
        </div>
      </section>

      <section className="home-actions">
        <h4>Quick actions</h4>
        <div className="home-actions-list">
          <button
            type="button"
            className="home-action-btn"
            onClick={() => window.alert("Open Visualization Tool")}
          >
            Open Visualization Tool
          </button>
          <button
            type="button"
            className="home-action-btn"
            onClick={() => window.alert("Open AI Recommendation System")}
          >
            Ask AI for datasets
          </button>
          <button
            type="button"
            className="home-action-btn"
            onClick={() => window.alert("Open Cloud Data Server")}
          >
            View Cloud Data Server
          </button>
        </div>
      </section>

      <section className="home-status">
        {isLive ? (
          <>Live stream · Fetching from FastAPI backend every 3 seconds · Endpoint: {BACKEND_URL}</>
        ) : (
          <>Offline · Unable to connect to backend · Check if FastAPI server is running at {BACKEND_URL}</>
        )}
      </section>
    </div>
  );
};

export default ScientistHomeModule;
