// src/modules/ScientistHomeModule.jsx
import React, { useEffect, useState, useMemo, useRef } from "react";
import axios from "axios";
import "./ScientistHomeModule.css";

// how many points to keep on the screen
const POINT_COUNT = 40;

// Backend URL - update this to your FastAPI endpoint
const BACKEND_URL = "http://10.121.243.94:8000/visualize/map";

// Available parameters
const PARAMETERS = [
  { value: "DIC", label: "DIC" },
  { value: "MLD", label: "MLD" },
  { value: "PCO2_ORIGINAL", label: "PCO2_ORIGINAL" },
  { value: "CHL", label: "CHL" },
  { value: "NO3", label: "NO3" },
  { value: "SSS", label: "SSS" },
  { value: "SST", label: "SST" },
  { value: "DEVIANT_UNCERTAINTY", label: "DEVIANT_UNCERTAINTY" },
];

// create initial empty series
const createInitialSeries = () => {
  return Array.from({ length: POINT_COUNT }, (_, i) => ({
    x: i,
    value: 27, // default value
  }));
};

const ScientistHomeModule = () => {
  const [series, setSeries] = useState(createInitialSeries);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [isLive, setIsLive] = useState(false);
  const [parameter, setParameter] = useState("SST");
  const [plotImage, setPlotImage] = useState(null);
  const mountedRef = useRef(true);
  const controllerRef = useRef(null);

  // Fetch real-time data from FastAPI backend
  const fetchRealtimeData = async () => {
    try {
      const response = await axios.get(BACKEND_URL, {
        params: { parameter },
        timeout: 5000,
      });

      if (!mountedRef.current) return;

      // Expecting response format: { value: number, timestamp: string }
      // or { data: [{ value: number, timestamp: string }, ...] }
      const data = response.data;
      
      if (data.value !== undefined) {
        // Single value response
        setSeries((prev) => {
          const last = prev[prev.length - 1];
          const nextX = last.x + 1;
          const sliced = prev.slice(1);
          return [...sliced, { x: nextX, value: data.value }];
        });
        setIsLive(true);
      } else if (Array.isArray(data.data)) {
        // Array of values response
        setSeries(data.data.map((item, i) => ({
          x: i,
          value: item.value || item.sst || 27,
        })).slice(-POINT_COUNT));
        setIsLive(true);
      }
      
      setError(null);
      setLoading(false);
    } catch (err) {
      if (!mountedRef.current) return;
      console.error("Failed to fetch real-time data:", err);
      setError(err.message || "Failed to connect to backend");
      setIsLive(false);
      setLoading(false);
    }
  };

  // Initial fetch and periodic updates
  useEffect(() => {
    mountedRef.current = true;
    
    // Initial fetch
    fetchRealtimeData();
    
    // Poll every 3 seconds for new data
    const id = setInterval(() => {
      fetchRealtimeData();
    }, 3000);

    return () => {
      mountedRef.current = false;
      clearInterval(id);
    };
  }, [parameter]);

  // compute SVG path
  const { path, minVal, maxVal } = useMemo(() => {
    if (!series.length) return { path: "", minVal: 0, maxVal: 1 };

    const values = series.map((d) => d.value);
    const minV = Math.min(...values) - 0.3;
    const maxV = Math.max(...values) + 0.3;

    const width = 900;
    const height = 260;
    const paddingLeft = 40;
    const paddingRight = 10;
    const paddingTop = 20;
    const paddingBottom = 30;

    const usableWidth = width - paddingLeft - paddingRight;
    const usableHeight = height - paddingTop - paddingBottom;

    const xScale = (i) =>
      paddingLeft +
      (i / Math.max(series.length - 1, 1)) * usableWidth;

    const yScale = (v) => {
      const norm =
        (v - minV) / Math.max(maxV - minV, 0.0001); // 0 → 1
      return paddingTop + (1 - norm) * usableHeight;
    };

    let p = "";
    series.forEach((pt, i) => {
      const x = xScale(i);
      const y = yScale(pt.value);
      p += i === 0 ? `M ${x} ${y}` : ` L ${x} ${y}`;
    });

    return { path: p, minVal: minV, maxVal: maxV };
  }, [series]);

  return (
    <div className="scientist-home">
      {/* HEADER */}
      <header className="scientist-home-header">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "12px" }}>
          <h3>Real-time Oceanography Snapshot</h3>
          <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
            <label htmlFor="parameter-select" style={{ fontSize: "0.9rem", color: "#9ca3af" }}>
              Choose Parameter:
            </label>
            <select
              id="parameter-select"
              value={parameter}
              onChange={(e) => setParameter(e.target.value)}
              style={{
                padding: "6px 12px",
                borderRadius: "8px",
                border: "1px solid rgba(148, 163, 184, 0.3)",
                background: "rgba(15, 23, 42, 0.85)",
                color: "#e5e7eb",
                fontSize: "0.9rem",
                cursor: "pointer",
              }}
            >
              {PARAMETERS.map((param) => (
                <option key={param.value} value={param.value}>
                  {param.label}
                </option>
              ))}
            </select>
          </div>
        </div>
        <p>
          Real-time {parameter} data stream for the Indian EEZ. Plot updates automatically every 3 seconds from backend.
        </p>
      </header>

      {/* REAL-TIME PLOT */}
      <section className="realtime-plot-container">
        <div className="realtime-plot-inner">
          <div className="realtime-plot-header-row">
            <div>
              <p className="realtime-plot-title">
                {parameter} · Real-time Stream
              </p>
              <p className="realtime-plot-sub">
                {error ? `Error: ${error}` : 'Real-time data from FastAPI backend'}
              </p>
            </div>
            <span className={`realtime-badge ${isLive ? 'live' : 'offline'}`}>
              {loading ? 'Connecting...' : isLive ? `Live · ${series[series.length - 1]?.value.toFixed(2)}°C` : 'Offline'}
            </span>
          </div>

          <svg
            className="realtime-plot-svg"
            viewBox="0 0 900 260"
            preserveAspectRatio="none"
          >
            {/* gradient background */}
            <defs>
              <linearGradient id="sstLine" x1="0" y1="0" x2="1" y2="0">
                <stop offset="0%" stopColor="#22d3ee" />
                <stop offset="100%" stopColor="#38bdf8" />
              </linearGradient>
              <linearGradient id="sstFill" x1="0" y1="0" x2="0" y2="1">
                <stop offset="0%" stopColor="rgba(34,211,238,0.35)" />
                <stop offset="100%" stopColor="rgba(15,23,42,0.0)" />
              </linearGradient>
            </defs>

            {/* under-area */}
            {path && (
              <>
                <path
                  d={`${path} L 890 260 L 40 260 Z`}
                  fill="url(#sstFill)"
                />
                <path
                  d={path}
                  fill="none"
                  stroke="url(#sstLine)"
                  strokeWidth="2.2"
                  strokeLinejoin="round"
                  strokeLinecap="round"
                />
              </>
            )}

            {/* simple Y ticks on left */}
            {Array.from({ length: 4 }).map((_, i) => {
              const t = i / 3;
              const value = (minVal + t * (maxVal - minVal)).toFixed(1);
              const y = 40 + (1 - t) * 180;
              return (
                <g key={i}>
                  <line
                    x1={40}
                    x2={880}
                    y1={y}
                    y2={y}
                    stroke="rgba(148,163,184,0.18)"
                    strokeWidth="0.8"
                    strokeDasharray="3 4"
                  />
                  <text
                    x={10}
                    y={y + 4}
                    fontSize="10"
                    fill="#9ca3af"
                  >
                    {value}°C
                  </text>
                </g>
              );
            })}
          </svg>
        </div>
      </section>

      {/* METRICS + ACTIONS ROW */}
      <section className="home-metrics-grid">
        <div className="home-metric-card">
          <p className="home-metric-label">Current {parameter}</p>
          <p className="home-metric-value">
            {series[series.length - 1]?.value.toFixed(2)}
          </p>
          <p className="home-metric-note">
            Latest value from real-time stream.
          </p>
        </div>

        <div className="home-metric-card">
          <p className="home-metric-label">Last Δ{parameter}</p>
          <p className="home-metric-value">
            {(
              series[series.length - 1].value -
              series[series.length - 3].value
            ).toFixed(2)}
          </p>
          <p className="home-metric-note">
            Change over last two samples in the stream.
          </p>
        </div>
      </section>

      <section className="home-actions">
        <h4>Quick actions</h4>
        <div className="home-actions-list">
          <button
            type="button"
            className="home-action-btn"
            onClick={() => window.alert("Open Visualization Tool")}
          >
            Open Visualization Tool
          </button>
          <button
            type="button"
            className="home-action-btn"
            onClick={() => window.alert("Open AI Recommendation System")}
          >
            Ask AI for datasets
          </button>
          <button
            type="button"
            className="home-action-btn"
            onClick={() => window.alert("Open Cloud Data Server")}
          >
            View Cloud Data Server
          </button>
        </div>
      </section>

      <section className="home-status">
        {isLive ? (
          <>Live stream · Fetching from FastAPI backend every 3 seconds · Endpoint: {BACKEND_URL}</>
        ) : (
          <>Offline · Unable to connect to backend · Check if FastAPI server is running at {BACKEND_URL}</>
        )}
      </section>
    </div>
  );
};

export default ScientistHomeModule;
