// VisualizationTool.js
import React, { useCallback, useEffect, useMemo, useRef, useState } from "react";
import "./VisualizationTool.css";
import VisualizationParametersPanel from "./VisualizationParametersPanel";
import axios from "axios";

/*
  Merged behavior:
  - Domain dropdown: Oceanography | Biodiversity
  - Oceanography: unchanged (existing behavior)
  - Biodiversity:
      -> Mode selector inside Biodiversity: "Taxonomy Map" | "Dynamic Plot"
      -> Taxonomy Map: copied logic from BiodiversityPlot.js (family input, Generate Plot -> blob / Open Map Here -> iframe)
      -> Dynamic Plot: copied logic from ScientistHomeModule.js adapted to be available under Biodiversity.
*/

const PARAMETERS = [
  { value: "DIC", label: "DIC" },
  { value: "MLD", label: "MLD" },
  { value: "PCO2_ORIGINAL", label: "PCO2_ORIGINAL" },
  { value: "CHL", label: "CHL" },
  { value: "NO3", label: "NO3" },
  { value: "SSS", label: "SSS" },
  { value: "SST", label: "SST" },
  { value: "DEVIANT_UNCERTAINTY", label: "DEVIANT_UNCERTAINTY" },
];

// Endpoints (keep same hosts as original files)
const BASE_OCEAN_BACKEND = "http://192.168.0.2:8000/ocean-overlay/multi";
const BASE_BIODIVERSITY_BACKEND = "http://192.168.0.2:8000/biodiversity";
const TAXONOMY_URL = "http://192.168.0.3:8000/taxonomy/map"; // from BiodiversityPlot.js
const DYNAMIC_BACKEND_REALTIME = "http://192.168.0.2:8000/visualize/map"; // from ScientistHomeModule.js
const DYNAMIC_BACKEND_HEATMAP = "http://192.168.0.2:8000/ocean-heatmap/plot";

const VisualizationTool = () => {
  // -----------------------------
  // Shared state
  // -----------------------------
  const [timeRange, setTimeRange] = useState("last7");
  const [time1, setTime1] = useState("");
  const [time2, setTime2] = useState("");
  const [vizParams, setVizParams] = useState({
    independentVariable: "Time",
    parameters: ["SST"],
  });
  const [plotType, setPlotType] = useState("line");
  const [imageUrl, setImageUrl] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const controllerRef = useRef(null);
  const mountedRef = useRef(true);

  // Domain only: Oceanography | Biodiversity
  const [domain, setDomain] = useState("Oceanography");

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
      if (controllerRef.current) {
        try {
          controllerRef.current.abort();
        } catch (e) {}
      }
      if (imageUrl) {
        try {
          URL.revokeObjectURL(imageUrl);
        } catch (e) {}
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // -----------------------------
  // Date helpers
  // -----------------------------
  const toDDMMYYYY = useCallback((d) => {
    const dt = new Date(d);
    const yyyy = dt.getFullYear();
    const mm = String(dt.getMonth() + 1).padStart(2, "0");
    const dd = String(dt.getDate()).padStart(2, "0");
    return `${dd}/${mm}/${yyyy}`;
  }, []);

  const setLastNDays = useCallback(
    (n) => {
      const now = new Date();
      const endStr = toDDMMYYYY(now);
      const start = new Date(now);
      start.setDate(start.getDate() - n);
      const startStr = toDDMMYYYY(start);
      setTime1(startStr);
      setTime2(endStr);
    },
    [toDDMMYYYY]
  );

  useEffect(() => {
    if (timeRange === "last7") setLastNDays(7);
    else if (timeRange === "last30") setLastNDays(30);
    else if (timeRange === "custom") {
      if (!time1) setTime1(toDDMMYYYY(new Date()));
      if (!time2) setTime2(toDDMMYYYY(new Date()));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [timeRange]);

  useEffect(() => {
    setLastNDays(7);
  }, [setLastNDays]);

  const toYYYYMMDD = useCallback((ddmmyyyy) => {
    if (!ddmmyyyy) return "";
    const parts = ddmmyyyy.split("/");
    if (parts.length !== 3) return ddmmyyyy;
    const [dd, mm, yyyy] = parts;
    return `${yyyy}-${mm}-${dd}`;
  }, []);

  // -----------------------------
  // Field mapping & options
  // -----------------------------
  const fieldMap = useMemo(
    () => ({
      Time: "datetime",
      Latitude: "lat",
      Longitude: "lon",
      DIC: "dic",
      MLD: "mld",
      PCO2_ORIGINAL: "pco2_original",
      CHL: "chl",
      NO3: "no3",
      SSS: "sss",
      SST: "sst",
      DEVIANT_UNCERTAINTY: "deviant_uncertainty",
    }),
    []
  );

  const xOptions = useMemo(() => ["Latitude", "Longitude", "Time"], []);
  const yOptions = useMemo(
    () => [
      "DIC",
      "MLD",
      "PCO2_ORIGINAL",
      "CHL",
      "NO3",
      "SSS",
      "SST",
      "DEVIANT_UNCERTAINTY",
    ],
    []
  );
  const initialY = useMemo(() => ["SST"], []);

  const handleVizParamsChange = useCallback((payload) => {
    setVizParams((prev) => {
      try {
        if (JSON.stringify(prev) === JSON.stringify(payload)) return prev;
      } catch (e) {}
      return payload;
    });
  }, []);

  // -----------------------------
  // Biodiversity-specific: taxonomy map (copied from BiodiversityPlot.js)
  // -----------------------------
  const [bioFamily, setBioFamily] = useState("");
  const [bioPlotImage, setBioPlotImage] = useState(null);
  const [bioLivePlotUrl, setBioLivePlotUrl] = useState(null);
  const bioControllerRef = useRef(null);
  const bioMountedRef = useRef(true);
  const [bioLoading, setBioLoading] = useState(false);
  const [bioError, setBioError] = useState(null);

  useEffect(() => {
    bioMountedRef.current = true;
    return () => {
      bioMountedRef.current = false;
      if (bioControllerRef.current) try { bioControllerRef.current.abort(); } catch (e) {}
      if (bioPlotImage) try { URL.revokeObjectURL(bioPlotImage); } catch (e) {}
    };
  }, []);

  const bioGeneratePlot = async () => {
    setBioError(null);
    const familyTrimmed = (bioFamily || "").trim();
    if (!familyTrimmed) {
      setBioError("Please enter a family name (example: Acoetidae)");
      return;
    }

    setBioLoading(true);
    setBioLivePlotUrl(null); // hide iframe

    // clear previous blob URL
    if (bioPlotImage) {
      try { URL.revokeObjectURL(bioPlotImage); } catch {}
      setBioPlotImage(null);
    }

    // cancel previous request
    if (bioControllerRef.current) {
      try { bioControllerRef.current.abort(); } catch {}
    }

    const controller = new AbortController();
    bioControllerRef.current = controller;

    try {
      const response = await axios.get(TAXONOMY_URL, {
        params: { family: familyTrimmed },
        responseType: "blob",
        timeout: 30000,
        signal: controller.signal,
      });

      if (!bioMountedRef.current) return;

      const blob = response.data;
      if (!blob) throw new Error("No image received from backend.");

      const url = URL.createObjectURL(blob);
      setBioPlotImage(url);
      setBioError(null);
    } catch (err) {
      if (!bioMountedRef.current) return;

      if (axios.isCancel?.(err)) {
        console.log("Request cancelled");
      } else {
        console.error("Plot error:", err);
        if (err.response) {
          setBioError(`Server returned ${err.response.status}: ${err.response.statusText}`);
        } else if (err.request) {
          setBioError("No response from server.");
        } else {
          setBioError(err.message || "Unknown error");
        }
      }
    } finally {
      setBioLoading(false);
    }
  };

  const bioOpenMapHere = () => {
    const familyTrimmed = (bioFamily || "").trim();
    if (!familyTrimmed) {
      setBioError("Please enter a family name first.");
      return;
    }

    setBioError(null);
    const url = `${TAXONOMY_URL}?family=${encodeURIComponent(familyTrimmed)}`;
    setBioLivePlotUrl(url);

    // hide blob image
    if (bioPlotImage) {
      try { URL.revokeObjectURL(bioPlotImage); } catch {}
      setBioPlotImage(null);
    }
  };

  // -----------------------------
  // Biodiversity-specific: dynamic plot (copied/adapted from ScientistHomeModule.js)
  // Provides a realtime | heatmap mode with parameter select
  // -----------------------------
  const [bioDynamicMode, setBioDynamicMode] = useState("realtime"); // 'realtime' | 'heatmap'
  const [bioDynamicParam, setBioDynamicParam] = useState("DEVIANT_UNCERTAINTY");
  const [bioDynPlotImage, setBioDynPlotImage] = useState(null);
  const [bioDynLiveUrl, setBioDynLiveUrl] = useState(null);
  const [bioDynLoading, setBioDynLoading] = useState(false);
  const bioDynControllerRef = useRef(null);
  const bioDynMountedRef = useRef(true);
  const [bioDynError, setBioDynError] = useState(null);

  useEffect(() => {
    bioDynMountedRef.current = true;
    return () => {
      bioDynMountedRef.current = false;
      if (bioDynControllerRef.current) try { bioDynControllerRef.current.abort(); } catch (e) {}
      if (bioDynPlotImage) try { URL.revokeObjectURL(bioDynPlotImage); } catch (e) {}
    };
  }, []);

  const bioDynGenerateRealtime = async () => {
    setBioDynError(null);
    setBioDynLoading(true);

    // clear iframe
    setBioDynLiveUrl(null);

    if (bioDynPlotImage) {
      try { URL.revokeObjectURL(bioDynPlotImage); } catch (e) {}
      setBioDynPlotImage(null);
    }

    if (bioDynControllerRef.current) {
      try { bioDynControllerRef.current.abort(); } catch (e) {}
    }
    const controller = new AbortController();
    bioDynControllerRef.current = controller;

    try {
      const response = await axios.get(DYNAMIC_BACKEND_REALTIME, {
        params: { parameter: bioDynamicParam },
        responseType: "blob",
        timeout: 30000,
        signal: controller.signal,
        headers: { Accept: "image/png, image/jpeg, application/octet-stream" },
      });

      if (!bioDynMountedRef.current) return;

      const blob = response?.data;
      if (!blob) throw new Error("No image data received from server.");

      const url = URL.createObjectURL(blob);
      setBioDynPlotImage(url);
      setBioDynError(null);
    } catch (err) {
      if (!bioDynMountedRef.current) return;
      if (axios.isCancel?.(err)) {
        console.log("Request cancelled");
      } else {
        console.error("Failed to generate realtime plot:", err);
        if (err.response) {
          setBioDynError(`Server returned ${err.response.status}: ${err.response.statusText}`);
        } else if (err.request) {
          setBioDynError("No response from server. Confirm backend is running and accessible.");
        } else {
          setBioDynError(err.message || "Unknown error");
        }
      }
    } finally {
      if (bioDynMountedRef.current) setBioDynLoading(false);
    }
  };

  const bioDynOpenHeatmap = () => {
    setBioDynError(null);
    if (bioDynPlotImage) {
      try { URL.revokeObjectURL(bioDynPlotImage); } catch (e) {}
      setBioDynPlotImage(null);
    }
    const qParam = encodeURIComponent((bioDynamicParam || "").toLowerCase());
    const url = `${DYNAMIC_BACKEND_HEATMAP}?param=${qParam}`;
    setBioDynLiveUrl(url);
  };

  const bioDynOpenMapHere = () => {
    const url = `${DYNAMIC_BACKEND_REALTIME}?parameter=${encodeURIComponent(bioDynamicParam)}`;
    setBioDynLiveUrl(url);
    if (bioDynPlotImage) {
      try { URL.revokeObjectURL(bioDynPlotImage); } catch (e) {}
      setBioDynPlotImage(null);
    }
  };

  // -----------------------------
  // Build payload (Oceanography unchanged)
  // -----------------------------
  const buildPayloadAndParams = useCallback(() => {
    const body = { domain };
    const params = { domain };

    if (domain === "Oceanography") {
      const parameters = vizParams?.parameters || [];
      const start_date = toYYYYMMDD(time1);
      const end_date = toYYYYMMDD(time2);
      const x_axis =
        fieldMap[vizParams.independentVariable] || vizParams.independentVariable;
      const y_axes = parameters.map((p) => fieldMap[p] || p);

      body.start_date = start_date;
      body.end_date = end_date;
      body.time_range = timeRange;
      body.x_axis = x_axis;
      body.y_axes = y_axes;
      body.x_label = vizParams.independentVariable;
      body.y_labels = parameters;
      body.plot_type = plotType;

      params.plot_type = plotType;
      params.x = x_axis;
      params.y = y_axes;
      params.start_date = start_date;
      params.end_date = end_date;
    } else {
      body.plot_type = plotType;
      params.plot_type = plotType;
    }

    return { body, params };
  }, [domain, vizParams, time1, time2, timeRange, fieldMap, toYYYYMMDD, plotType]);

  // -----------------------------
  // Apply -> request backend
  // -----------------------------
  const handleApply = useCallback(async () => {
    setError(null);

    if (domain === "Oceanography") {
      const parameters = vizParams?.parameters || [];
      if (!parameters || parameters.length === 0) {
        setError(new Error("Select at least one Y parameter."));
        return;
      }
    }

    if (imageUrl) {
      try {
        URL.revokeObjectURL(imageUrl);
      } catch (e) {}
      setImageUrl(null);
    }

    setLoading(true);

    const { body, params } = buildPayloadAndParams();

    if (controllerRef.current) {
      try {
        controllerRef.current.abort();
      } catch (e) {}
    }
    const controller = new AbortController();
    controllerRef.current = controller;

    try {
      const postConfig = {
        responseType: "blob",
        timeout: 45000,
        signal: controller.signal,
        headers: {
          "Content-Type": "application/json",
          Accept: "image/png, image/jpeg, application/octet-stream",
        },
      };

      if (domain === "Biodiversity") {
        // Request the biodiversity backend (it may accept body or query params)
        try {
          const resp = await axios.post(BASE_BIODIVERSITY_BACKEND, body, postConfig);
          if (!mountedRef.current) return;
          const blob = resp?.data;
          if (blob) {
            const url = URL.createObjectURL(blob);
            setImageUrl(url);
            setError(null);
            return;
          }
        } catch (errB) {
          // fallback to GET with query params
          const q = [];
          if (params.plot_type) q.push(`plot_type=${encodeURIComponent(params.plot_type)}`);
          if (params.domain) q.push(`domain=${encodeURIComponent(params.domain)}`);
          const urlWithQuery = `${BASE_BIODIVERSITY_BACKEND}?${q.join("&")}`;
          const respGet = await axios.get(urlWithQuery, {
            responseType: "blob",
            timeout: 45000,
            signal: controller.signal,
          });
          if (!mountedRef.current) return;
          const blob = respGet?.data;
          if (blob) {
            const url = URL.createObjectURL(blob);
            setImageUrl(url);
            setError(null);
            return;
          }
        }
      } else {
        // Oceanography
        try {
          const resp = await axios.post(BASE_OCEAN_BACKEND, body, postConfig);
          if (!mountedRef.current) return;
          const blob = resp?.data;
          if (blob) {
            const url = URL.createObjectURL(blob);
            setImageUrl(url);
            setError(null);
            return;
          }
        } catch (postErr) {
          if (postErr?.response?.status === 405) {
            const queryParts = [];
            queryParts.push(`plot_type=${encodeURIComponent(params.plot_type)}`);
            queryParts.push(`x=${encodeURIComponent(params.x)}`);
            if (Array.isArray(params.y)) {
              params.y.forEach(yVal => queryParts.push(`y=${encodeURIComponent(yVal)}`));
            }
            if (params.start_date) queryParts.push(`start_date=${encodeURIComponent(params.start_date)}`);
            if (params.end_date) queryParts.push(`end_date=${encodeURIComponent(params.end_date)}`);
            if (params.domain) queryParts.push(`domain=${encodeURIComponent(params.domain)}`);
            const queryString = queryParts.join("&");
            const urlWithQuery = `${BASE_OCEAN_BACKEND}?${queryString}`;
            const respGet = await axios.get(urlWithQuery, {
              responseType: "blob",
              timeout: 45000,
              signal: controller.signal,
            });
            if (!mountedRef.current) return;
            const blob = respGet?.data;
            if (blob) {
              const url = URL.createObjectURL(blob);
              setImageUrl(url);
              setError(null);
              return;
            }
          } else {
            throw postErr;
          }
        }
      }

      throw new Error("No valid response from server.");
    } catch (err) {
      if (!mountedRef.current) return;
      if (axios.isCancel?.(err)) {
        console.log("Request cancelled");
      } else {
        console.error("Failed to fetch image:", err);
        if (err.response) setError(new Error(`Server returned ${err.response.status}: ${err.response.statusText}`));
        else if (err.request) setError(new Error("No response from server. Check backend & CORS."));
        else setError(new Error(err.message || "Unknown error"));
      }
    } finally {
      if (mountedRef.current) setLoading(false);
    }
  }, [vizParams, imageUrl, buildPayloadAndParams, domain]);

  // -----------------------------
  // UI - Render
  // -----------------------------
  return (
    <div className="viz-layout reverse-layout">
      {/* RIGHT: FILTERS */}
      <section className="viz-panel viz-panel-left">
        <h3 className="viz-panel-title">Data & Filters</h3>
        <p className="viz-panel-text">Select domain, region, time window and plot type to request a plot image from the backend.</p>

        {/* DOMAIN DROPDOWN */}
        <div className="viz-form-group">
          <label>Domain</label>
          <select value={domain} onChange={(e) => setDomain(e.target.value)}>
            <option>Oceanography</option>
            <option>Biodiversity</option>
          </select>
        </div>

        {/* Only for Oceanography: Region, Time Range, Visualization Parameters */}
        {domain === "Oceanography" && (
          <>
            <div className="viz-form-group">
              <label>Region</label>
              <select>
                <option>Entire Indian EEZ</option>
                <option>Arabian Sea</option>
                <option>Bay of Bengal</option>
                <option>Andaman Sea</option>
              </select>
            </div>

            <div className="viz-form-group">
              <label>Time Range</label>
              <select value={timeRange} onChange={(e) => setTimeRange(e.target.value)}>
                <option value="last7">Last 7 days</option>
                <option value="last30">Last 30 days</option>
                <option value="custom">Custom</option>
              </select>
            </div>

            {timeRange === "custom" && (
              <>
                <div className="viz-form-group">
                  <label>Start date</label>
                  <input type="date" value={toYYYYMMDD(time1)} onChange={(e) => {
                    const yyyymmdd = e.target.value;
                    if (yyyymmdd) {
                      const [yyyy, mm, dd] = yyyymmdd.split("-");
                      setTime1(`${dd}/${mm}/${yyyy}`);
                    } else setTime1("");
                  }} className="date-input" />
                </div>

                <div className="viz-form-group">
                  <label>End date</label>
                  <input type="date" value={toYYYYMMDD(time2)} onChange={(e) => {
                    const yyyymmdd = e.target.value;
                    if (yyyymmdd) {
                      const [yyyy, mm, dd] = yyyymmdd.split("-");
                      setTime2(`${dd}/${mm}/${yyyy}`);
                    } else setTime2("");
                  }} className="date-input" />
                </div>
              </>
            )}

            <div style={{ marginTop: 18 }}>
              <VisualizationParametersPanel xOptions={xOptions} yOptions={yOptions} initialX="Time" initialY={initialY} onChange={handleVizParamsChange} />
            </div>
          </>
        )}

        {/* Biodiversity domain: two internal modes */}
        {domain === "Biodiversity" && (
          <>
            <div className="viz-form-group">
              <label>Biodiversity View</label>
              <select value={/* either taxonomy vs dynamic */ (bioLivePlotUrl || bioPlotImage) ? "taxonomy" : bioDynamicMode} onChange={(e) => {
                const v = e.target.value;
                // if user chooses 'taxonomy' we'll set default - otherwise dynamic
                if (v === "taxonomy") {
                  // nothing special
                }
                setBioError(null);
                setBioPlotImage(null);
                setBioLivePlotUrl(null);
                setBioDynPlotImage(null);
                setBioDynLiveUrl(null);
                setBioDynError(null);
                setBioDynamicMode(v === "dynamic" ? "realtime" : "realtime");
              }}>
                <option value="taxonomy">Taxonomy Map (by Family)</option>
                <option value="dynamic">Dynamic Plot (Realtime / Heatmap)</option>
              </select>
            </div>

            {/* Taxonomy Map controls */}
            <div style={{ marginTop: 12 }}>
              <label style={{ display: "block", marginBottom: 6 }}>Enter Family Name (Taxonomy)</label>
              <input
                type="text"
                placeholder="Family Name"
                value={bioFamily}
                onChange={(e) => setBioFamily(e.target.value)}
                className="taxonomy-text-input"
              />
              <div style={{ marginTop: 10, display: "flex", gap: 8 }}>
                <button className="viz-apply-btn" onClick={bioGeneratePlot} disabled={bioLoading}>{bioLoading ? "Generating..." : "Generate Plot"}</button>
                <button className="viz-apply-btn secondary" onClick={bioOpenMapHere}>Open Map Here</button>
              </div>
              <div className="viz-hint" style={{ marginTop: 8 }}>Taxonomy map (backend: {TAXONOMY_URL}).</div>
              {bioError && <div className="viz-error" style={{ marginTop: 8, color: "#ffbdbd" }}>{bioError}</div>}
            </div>

            <hr style={{ margin: "16px 0", border: "none", borderTop: "1px solid rgba(148,163,184,0.06)" }} />

            {/* Dynamic Plot controls (copied from ScientistHomeModule.js) */}
            <div style={{ marginTop: 6 }}>
              <div style={{ display: "flex", gap: 8, alignItems: "center", marginBottom: 8 }}>
                <label style={{ minWidth: 80 }}>Mode</label>
                <select value={bioDynamicMode} onChange={(e) => {
                  setBioDynamicMode(e.target.value);
                  setBioDynError(null);
                  setBioDynPlotImage(null);
                  setBioDynLiveUrl(null);
                }}>
                  <option value="realtime">Realtime</option>
                  <option value="heatmap">Heatmap</option>
                </select>

                <label style={{ marginLeft: 8 }}>Parameter</label>
                <select value={bioDynamicParam} onChange={(e) => setBioDynamicParam(e.target.value)}>
                  {PARAMETERS.map(p => <option key={p.value} value={p.value}>{p.label}</option>)}
                </select>
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                {bioDynamicMode === "realtime" ? (
                  <>
                    <button className="viz-apply-btn" onClick={bioDynGenerateRealtime} disabled={bioDynLoading}>{bioDynLoading ? "Generating..." : "Generate Plot"}</button>
                    <button className="viz-apply-btn secondary" onClick={bioDynOpenMapHere}>Open Map Here</button>
                  </>
                ) : (
                  <>
                    <button className="viz-apply-btn" onClick={bioDynOpenHeatmap}>Open Heatmap</button>
                    <button className="viz-apply-btn secondary" onClick={() => window.open(`${DYNAMIC_BACKEND_HEATMAP}?param=${encodeURIComponent(bioDynamicParam.toLowerCase())}`, "_blank", "noopener")}>Open in New Tab</button>
                  </>
                )}
              </div>

              {bioDynError && <div className="viz-error" style={{ marginTop: 8, color: "#ffbdbd" }}>{bioDynError}</div>}
            </div>
          </>
        )}

        {/* Apply button & error for Oceanography / Biodiversity image generation */}
        <div style={{ marginTop: 14 }}>
          <button type="button" className="viz-apply-btn" onClick={handleApply} disabled={loading}>{loading ? "Generating imageâ€¦" : "Apply Filters"}</button>
          {error && <div className="viz-error" style={{ marginTop: 12, color: "#ffbdbd" }}>{error.message || "Failed to generate image. See console."}</div>}
        </div>
      </section>

      {/* LEFT: image/map view */}
      <section className="viz-panel viz-panel-main">
        <div className="viz-panel-header">
          <div>
            <h3 className="viz-panel-title">Map / Plot View</h3>
            <p className="viz-panel-text">The backend will return a plot image (PNG/JPEG) or an interactive iframe. It will be displayed here when ready.</p>
          </div>
        </div>

        <div className="viz-map-placeholder" style={{ display: "flex", alignItems: "center", justifyContent: "center", minHeight: 360 }}>
          {/* Priority: Oceanography imageUrl (from handleApply) */}
          {imageUrl ? (
            <img src={imageUrl} alt="Generated plot" style={{ maxWidth: "100%", maxHeight: "640px", objectFit: "contain", borderRadius: 8, boxShadow: "0 6px 20px rgba(2,6,23,0.6)" }} />
          ) : loading ? (
            <div style={{ color: "#9fb4d8" }}>Generating imageâ€¦</div>
          ) : (
            <>
              {/* Biodiversity taxonomy image */}
              {bioPlotImage ? (
                <img src={bioPlotImage} alt="taxonomy-plot" style={{ width: "100%", maxHeight: 700, objectFit: "contain", borderRadius: 8, border: "1px solid rgba(148,163,184,0.2)" }} />
              ) : (
                <>
                  {/* Biodiversity dynamic realtime image */}
                  {bioDynPlotImage ? (
                    <img src={bioDynPlotImage} alt="biodyn-plot" style={{ width: "100%", maxHeight: 700, objectFit: "contain", borderRadius: 8, border: "1px solid rgba(148,163,184,0.2)" }} />
                  ) : (
                    <>
                      {/* iframe views for taxonomy / dynamic */}
                      {bioLivePlotUrl ? (
                        <iframe src={bioLivePlotUrl} title="taxonomy-map" style={{ width: "100%", height: 700, border: "1px solid rgba(148,163,184,0.2)", borderRadius: 8 }} />
                      ) : bioDynLiveUrl ? (
                        <iframe src={bioDynLiveUrl} title="biodyn-iframe" style={{ width: "100%", height: 700, border: "1px solid rgba(148,163,184,0.2)", borderRadius: 8 }} />
                      ) : (
                        // Default placeholder if nothing from biodiversity either
                        <div style={{ textAlign: "center", color: "#9fb4d8" }}>
                          <p style={{ marginBottom: 6 }}>Spatial visualization / plot will appear here</p>
                          <small>Use the controls on the right to request a plot or open a map.</small>
                        </div>
                      )}
                    </>
                  )}
                </>
              )}
            </>
          )}
        </div>
      </section>

      {/* BOTTOM metrics unchanged */}
      <section className="viz-panel viz-panel-bottom">
        <h3 className="viz-panel-title">Time Series & Indicators</h3>
        <p className="viz-panel-text">Summary metrics (static placeholders)</p>

        <div className="viz-metrics-grid">
          <div className="viz-metric-card">
            <p className="viz-metric-label">SST (Â°C)</p>
            <p className="viz-metric-value">27.3</p>
            <p className="viz-metric-note">Near long-term average.</p>
          </div>
          <div className="viz-metric-card">
            <p className="viz-metric-label">Chl-a (mg/mÂ³)</p>
            <p className="viz-metric-value">0.8</p>
            <p className="viz-metric-note">Elevated coastal bloom.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default VisualizationTool;
