/**
 * HeroCarousel - small, self-contained carousel component.
 * Assumes images are available at /assets/<filename>
 */
function HeroCarousel({
  images = [
    "/assets/banner1.jpeg",
    "/assets/banner2.jpeg",
    "/assets/banner3.png"
  ],
  height = 300,
  interval = 4500
}) {
  const [index, setIndex] = useState(0);
  const timerRef = useRef(null);

  useEffect(() => {
    startAutoplay();
    return () => clearInterval(timerRef.current);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [images.length]);

  function startAutoplay() {
    clearInterval(timerRef.current);
    timerRef.current = setInterval(() => {
      setIndex((i) => (i + 1) % images.length);
    }, interval);
  }

  function goTo(i) {
    clearInterval(timerRef.current);
    setIndex(((i % images.length) + images.length) % images.length);
    startAutoplay();
  }

  // inline styles so you don't need CSS edits
  const containerStyle = {
    width: "100%",
    height: typeof height === "number" ? `${height}px` : height,
    borderRadius: 8,
    overflow: "hidden",
    position: "relative",
    marginTop: 20,
    marginBottom: 20,
    boxShadow: "0 6px 20px rgba(0,0,0,0.5)",
    background: "#0b1116"
  };

  const trackStyle = {
    display: "flex",
    width: `${images.length * 100}%`,
    height: "100%",
    transform: `translateX(-${index * (100 / images.length)}%)`,
    transition: "transform 700ms cubic-bezier(.2,.9,.1,1)"
  };

  const slideStyle = {
    width: `${100 / images.length}%`,
    flexShrink: 0,
    height: "100%",
    position: "relative",
    overflow: "hidden"
  };

  const btnBase = {
    position: "absolute",
    top: "50%",
    transform: "translateY(-50%)",
    zIndex: 6,
    border: "none",
    background: "rgba(0,0,0,0.45)",
    color: "#e8fff7",
    width: 36,
    height: 36,
    borderRadius: "50%",
    cursor: "pointer",
    fontSize: 20,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    backdropFilter: "blur(4px)"
  };

  const dotsWrap = {
    position: "absolute",
    left: "50%",
    transform: "translateX(-50%)",
    bottom: 10,
    display: "flex",
    gap: 8,
    zIndex: 6
  };

  return (
    <div
      className="hero-carousel"
      style={containerStyle}
      onMouseEnter={() => clearInterval(timerRef.current)}
      onMouseLeave={() => startAutoplay()}
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === "ArrowLeft") goTo(index - 1);
        if (e.key === "ArrowRight") goTo(index + 1);
      }}
      aria-roledescription="carousel"
    >
      <div style={trackStyle}>
        {images.map((src, i) => (
          <div
            key={i}
            className="carousel-slide"
            role="group"
            aria-roledescription="slide"
            aria-label={`Slide ${i + 1} of ${images.length}`}
            style={slideStyle}
          >
            <img
              src={src}
              alt={`Banner ${i + 1}`}
              style={{
                width: "100%",
                height: "100%",
                objectFit: "cover",
                objectPosition: "center",
                display: "block"
              }}
              onError={(e) => {
                console.error(`Failed to load image: ${src}`);
                e.currentTarget.style.display = "none";
              }}
            />
          </div>
        ))}
      </div>

      <button
        aria-label="Previous"
        style={{ ...btnBase, left: 12 }}
        onClick={() => goTo(index - 1)}
      >
        ‹
      </button>

      <button
        aria-label="Next"
        style={{ ...btnBase, right: 12 }}
        onClick={() => goTo(index + 1)}
      >
        ›
      </button>

      <div style={dotsWrap}>
        {images.map((_, i) => (
          <button
            key={i}
            aria-label={`Go to slide ${i + 1}`}
            aria-pressed={i === index}
            onClick={() => goTo(i)}
            style={{
              width: 10,
              height: 10,
              borderRadius: "50%",
              border: "1px solid rgba(255,255,255,0.25)",
              background: i === index ? "#1fe0c8" : "transparent",
              cursor: "pointer",
              padding: 0
            }}
          />
        ))}
      </div>
    </div>
  );
}

