// VisualizationTool.js
import React, { useState, useEffect, useCallback, useMemo, useRef } from "react";
import "./VisualizationTool.css";
import VisualizationParametersPanel from "./VisualizationParametersPanel";
import axios from "axios";

const VisualizationTool = () => {
  // -----------------------------
  // State
  // -----------------------------
  const [timeRange, setTimeRange] = useState("last7");
  const [time1, setTime1] = useState("");
  const [time2, setTime2] = useState("");
  const [vizParams, setVizParams] = useState({
    independentVariable: "Time",
    parameters: ["SST"],
  });
  const [plotType, setPlotType] = useState("line");
  const [imageUrl, setImageUrl] = useState(null);
  const [jsonResult, setJsonResult] = useState(null); // parsed JSON for taxonomy responses
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const controllerRef = useRef(null);
  const mountedRef = useRef(true);

  // Domain & taxonomy UI state
  const [domain, setDomain] = useState("Oceanography");
  const [taxonomySelect, setTaxonomySelect] = useState(""); // "" | "list" | "species" | "filter"
  const [listCount, setListCount] = useState("");
  const [scientificName, setScientificName] = useState("");
  const [filterFamily, setFilterFamily] = useState("");
  const [filterGenus, setFilterGenus] = useState("");
  const [filterOrder, setFilterOrder] = useState("");

  useEffect(() => {
    mountedRef.current = true;
    return () => {
      mountedRef.current = false;
      if (controllerRef.current) {
        try {
          controllerRef.current.abort();
        } catch (e) {}
      }
      if (imageUrl) {
        try {
          URL.revokeObjectURL(imageUrl);
        } catch (e) {}
      }
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // -----------------------------
  // Date helpers
  // -----------------------------
  const toDDMMYYYY = useCallback((d) => {
    const dt = new Date(d);
    const yyyy = dt.getFullYear();
    const mm = String(dt.getMonth() + 1).padStart(2, "0");
    const dd = String(dt.getDate()).padStart(2, "0");
    return `${dd}/${mm}/${yyyy}`;
  }, []);

  const setLastNDays = useCallback(
    (n) => {
      const now = new Date();
      const endStr = toDDMMYYYY(now);
      const start = new Date(now);
      start.setDate(start.getDate() - n);
      const startStr = toDDMMYYYY(start);
      setTime1(startStr);
      setTime2(endStr);
    },
    [toDDMMYYYY]
  );

  useEffect(() => {
    if (timeRange === "last7") setLastNDays(7);
    else if (timeRange === "last30") setLastNDays(30);
    else if (timeRange === "custom") {
      if (!time1) setTime1(toDDMMYYYY(new Date()));
      if (!time2) setTime2(toDDMMYYYY(new Date()));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [timeRange]);

  useEffect(() => {
    setLastNDays(7);
  }, [setLastNDays]);

  const toYYYYMMDD = useCallback((ddmmyyyy) => {
    if (!ddmmyyyy) return "";
    const parts = ddmmyyyy.split("/");
    if (parts.length !== 3) return ddmmyyyy;
    const [dd, mm, yyyy] = parts;
    return `${yyyy}-${mm}-${dd}`;
  }, []);

  // -----------------------------
  // Field mapping & options
  // -----------------------------
  const fieldMap = useMemo(
    () => ({
      Time: "datetime",
      Latitude: "lat",
      Longitude: "lon",
      DIC: "dic",
      MLD: "mld",
      PCO2_ORIGINAL: "pco2_original",
      CHL: "chl",
      NO3: "no3",
      SSS: "sss",
      SST: "sst",
      DEVIANT_UNCERTAINTY: "deviant_uncertainty",
    }),
    []
  );

  const xOptions = useMemo(() => ["Latitude", "Longitude", "Time"], []);
  const yOptions = useMemo(
    () => [
      "DIC",
      "MLD",
      "PCO2_ORIGINAL",
      "CHL",
      "NO3",
      "SSS",
      "SST",
      "DEVIANT_UNCERTAINTY",
    ],
    []
  );
  const initialY = useMemo(() => ["SST"], []);

  const handleVizParamsChange = useCallback((payload) => {
    setVizParams((prev) => {
      try {
        if (JSON.stringify(prev) === JSON.stringify(payload)) return prev;
      } catch (e) {}
      return payload;
    });
  }, []);

  // -----------------------------
  // Build payload & params
  // -----------------------------
  const buildPayloadAndParams = useCallback(() => {
    const body = { domain };
    const params = { domain };

    if (domain === "Oceanography") {
      const parameters = vizParams?.parameters || [];
      const start_date = toYYYYMMDD(time1);
      const end_date = toYYYYMMDD(time2);
      const x_axis =
        fieldMap[vizParams.independentVariable] || vizParams.independentVariable;
      const y_axes = parameters.map((p) => fieldMap[p] || p);

      body.start_date = start_date;
      body.end_date = end_date;
      body.time_range = timeRange;
      body.x_axis = x_axis;
      body.y_axes = y_axes;
      body.x_label = vizParams.independentVariable;
      body.y_labels = parameters;
      body.plot_type = plotType;

      params.plot_type = plotType;
      params.x = x_axis;
      params.y = y_axes;
      params.start_date = start_date;
      params.end_date = end_date;
    } else {
      body.plot_type = plotType;
      params.plot_type = plotType;
    }

    // Taxonomy fields
    if (domain === "Taxonomy") {
      body.taxonomy_action = taxonomySelect || undefined;
      if (taxonomySelect === "list") {
        body.route = "/taxonomy/list";
        body.count = listCount || undefined;
      } else if (taxonomySelect === "species") {
        body.route = `/taxonomy/species/${encodeURIComponent(scientificName || "")}`;
        body.scientific_name = scientificName || undefined;
      } else if (taxonomySelect === "filter") {
        body.route = "/taxonomy/filter";
        body.family = filterFamily || undefined;
        body.genus = filterGenus || undefined;
        body.order = filterOrder || undefined;
      }
      if (body.route) params.route = body.route;
    }

    return { body, params };
  }, [
    domain,
    vizParams,
    time1,
    time2,
    timeRange,
    fieldMap,
    toYYYYMMDD,
    plotType,
    taxonomySelect,
    listCount,
    scientificName,
    filterFamily,
    filterGenus,
    filterOrder,
  ]);

  // -----------------------------
  // Apply -> request backend
  // -----------------------------
  const handleApply = useCallback(async () => {
    setError(null);

    // Validation by domain
    if (domain === "Oceanography") {
      const parameters = vizParams?.parameters || [];
      if (!parameters || parameters.length === 0) {
        setError(new Error("Select at least one Y parameter."));
        return;
      }
    } else if (domain === "Taxonomy") {
      if (!taxonomySelect) {
        setError(new Error("Select a taxonomy action from the dropdown."));
        return;
      }
      if (taxonomySelect === "species" && !scientificName) {
        setError(new Error("Enter Scientific Name for Species Taxonomy."));
        return;
      }
      if (taxonomySelect === "list" && !listCount) {
        setError(new Error("Enter number of species for List Species."));
        return;
      }
      if (
        taxonomySelect === "filter" &&
        !filterFamily &&
        !filterGenus &&
        !filterOrder
      ) {
        setError(
          new Error(
            "Enter at least one of Family / Genus / Order for Filter Taxonomy."
          )
        );
        return;
      }
    }

    // cleanup previous outputs
    if (imageUrl) {
      try {
        URL.revokeObjectURL(imageUrl);
      } catch (e) {}
      setImageUrl(null);
    }
    setJsonResult(null);

    setLoading(true);

    const baseOceanBackend = "http://192.168.0.2:8000/ocean-overlay/multi";
    const baseTaxonomyBackend = "http://192.168.0.2:8000/taxonomy";
    const baseBiodiversityBackend = "http://192.168.0.3:8000/biodiversity";

    const { body, params } = buildPayloadAndParams();

    if (controllerRef.current) {
      try {
        controllerRef.current.abort();
      } catch (e) {}
    }
    const controller = new AbortController();
    controllerRef.current = controller;

    try {
      // common POST config for blob fallback
      const postConfigBlob = {
        responseType: "blob",
        timeout: 45000,
        signal: controller.signal,
        headers: {
          "Content-Type": "application/json",
          Accept: "image/png, image/jpeg, application/octet-stream, application/json",
        },
      };

      // taxonomy branch: try JSON GET first, fallback to POST(JSON) then blob
      if (domain === "Taxonomy") {
        let route = body.route || "/taxonomy/list";
        // taxonomyUrl expects suffix after /taxonomy (we keep baseTaxonomyBackend as /taxonomy)
        const suffix = route.replace(/^\/taxonomy/, ""); // /list or /species/{name} or /filter
        const taxonomyUrl = `${baseTaxonomyBackend}${suffix}`;

        // Build query for GET where applicable
        let q = "";
        if (taxonomySelect === "filter") {
          const qp = [];
          if (filterFamily) qp.push(`family=${encodeURIComponent(filterFamily)}`);
          if (filterGenus) qp.push(`genus=${encodeURIComponent(filterGenus)}`);
          if (filterOrder) qp.push(`order=${encodeURIComponent(filterOrder)}`);
          q = qp.length ? `?${qp.join("&")}` : "";
        } else if (taxonomySelect === "list") {
          if (listCount) q = `?count=${encodeURIComponent(listCount)}`;
        }

        // Try GET expecting JSON
        try {
          const jsonResp = await axios.get(`${taxonomyUrl}${q}`, {
            responseType: "json",
            timeout: 45000,
            signal: controller.signal,
            headers: { Accept: "application/json, application/octet-stream" },
          });

          if (!mountedRef.current) return;

          if (jsonResp && jsonResp.data) {
            setJsonResult(jsonResp.data);
            setError(null);
            return;
          }
          // fallthrough if no json (rare)
        } catch (getErr) {
          // GET failed: try POST expecting JSON
          try {
            const postJsonResp = await axios.post(
              `${baseTaxonomyBackend}${suffix}`,
              body,
              {
                responseType: "json",
                timeout: 45000,
                signal: controller.signal,
                headers: {
                  "Content-Type": "application/json",
                  Accept: "application/json, application/octet-stream",
                },
              }
            );

            if (!mountedRef.current) return;

            if (postJsonResp && postJsonResp.data) {
              setJsonResult(postJsonResp.data);
              setError(null);
              return;
            }
            // fallthrough to blob fallback
          } catch (postErr) {
            // Try blob fallback (in case server returns a file)
            try {
              const blobResp = await axios.get(`${taxonomyUrl}${q}`, {
                responseType: "blob",
                timeout: 45000,
                signal: controller.signal,
              });
              if (!mountedRef.current) return;
              const blob = blobResp?.data;
              if (blob) {
                const url = URL.createObjectURL(blob);
                setImageUrl(url);
                setError(null);
                return;
              }
            } catch (finalErr) {
              // propagate original error
              throw postErr || getErr || finalErr;
            }
          }
        }
      } else if (domain === "Biodiversity") {
        // Biodiversity: try POST then GET fallback
        try {
          const resp = await axios.post(baseBiodiversityBackend, body, postConfigBlob);
          if (!mountedRef.current) return;
          const blob = resp?.data;
          if (blob) {
            const url = URL.createObjectURL(blob);
            setImageUrl(url);
            setError(null);
            return;
          }
        } catch (errB) {
          const q = [];
          if (params.plot_type) q.push(`plot_type=${encodeURIComponent(params.plot_type)}`);
          if (params.domain) q.push(`domain=${encodeURIComponent(params.domain)}`);
          const urlWithQuery = `${baseBiodiversityBackend}?${q.join("&")}`;
          const respGet = await axios.get(urlWithQuery, {
            responseType: "blob",
            timeout: 45000,
            signal: controller.signal,
          });
          if (!mountedRef.current) return;
          const blob = respGet?.data;
          if (blob) {
            const url = URL.createObjectURL(blob);
            setImageUrl(url);
            setError(null);
            return;
          }
        }
      } else {
        // Oceanography: existing POST -> fallback GET on 405
        try {
          const resp = await axios.post(baseOceanBackend, body, postConfigBlob);
          if (!mountedRef.current) return;
          const blob = resp?.data;
          if (blob) {
            const url = URL.createObjectURL(blob);
            setImageUrl(url);
            setError(null);
            return;
          }
        } catch (postErr) {
          if (postErr?.response?.status === 405) {
            const queryParts = [];
            queryParts.push(`plot_type=${encodeURIComponent(params.plot_type)}`);
            queryParts.push(`x=${encodeURIComponent(params.x)}`);
            if (Array.isArray(params.y)) {
              params.y.forEach((yVal) => queryParts.push(`y=${encodeURIComponent(yVal)}`));
            }
            if (params.start_date) queryParts.push(`start_date=${encodeURIComponent(params.start_date)}`);
            if (params.end_date) queryParts.push(`end_date=${encodeURIComponent(params.end_date)}`);
            if (params.domain) queryParts.push(`domain=${encodeURIComponent(params.domain)}`);
            const queryString = queryParts.join("&");
            const urlWithQuery = `${baseOceanBackend}?${queryString}`;
            const respGet = await axios.get(urlWithQuery, {
              responseType: "blob",
              timeout: 45000,
              signal: controller.signal,
            });
            if (!mountedRef.current) return;
            const blob = respGet?.data;
            if (blob) {
              const url = URL.createObjectURL(blob);
              setImageUrl(url);
              setError(null);
              return;
            }
          } else {
            throw postErr;
          }
        }
      }

      // If we reach here without return, something unexpected happened
      throw new Error("No valid response from server.");
    } catch (err) {
      if (!mountedRef.current) return;
      if (axios.isCancel?.(err)) {
        console.log("Request cancelled");
      } else {
        console.error("Failed to fetch:", err);
        if (err.response) setError(new Error(`Server returned ${err.response.status}: ${err.response.statusText}`));
        else if (err.request) setError(new Error("No response from server. Check backend & CORS."));
        else setError(new Error(err.message || "Unknown error"));
      }
    } finally {
      if (mountedRef.current) setLoading(false);
    }
  }, [
    domain,
    vizParams,
    taxonomySelect,
    listCount,
    scientificName,
    filterFamily,
    filterGenus,
    filterOrder,
    imageUrl,
    buildPayloadAndParams,
  ]);

  // -----------------------------
  // UI - Render
  // -----------------------------
  return (
    <div className="viz-layout reverse-layout">
      {/* RIGHT: FILTERS */}
      <section className="viz-panel viz-panel-left">
        <h3 className="viz-panel-title">Data & Filters</h3>
        <p className="viz-panel-text">Select domain and action; taxonomy options appear when Domain = Taxonomy.</p>

        {/* Domain select */}
        <div className="viz-form-group">
          <label>Domain</label>
          <select
            value={domain}
            onChange={(e) => {
              const v = e.target.value;
              setDomain(v);
              // clear taxonomy selections when switching away
              if (v !== "Taxonomy") {
                setTaxonomySelect("");
                setListCount("");
                setScientificName("");
                setFilterFamily("");
                setFilterGenus("");
                setFilterOrder("");
                setJsonResult(null);
              }
            }}
          >
            <option>Oceanography</option>
            <option>Biodiversity</option>
            <option>Taxonomy</option>
          </select>
        </div>

        {/* Oceanography only */}
        {domain === "Oceanography" && (
          <>
            <div className="viz-form-group">
              <label>Region</label>
              <select>
                <option>Entire Indian EEZ</option>
                <option>Arabian Sea</option>
                <option>Bay of Bengal</option>
                <option>Andaman Sea</option>
              </select>
            </div>

            <div className="viz-form-group">
              <label>Time Range</label>
              <select value={timeRange} onChange={(e) => setTimeRange(e.target.value)}>
                <option value="last7">Last 7 days</option>
                <option value="last30">Last 30 days</option>
                <option value="custom">Custom</option>
              </select>
            </div>

            {timeRange === "custom" && (
              <>
                <div className="viz-form-group">
                  <label>Start date</label>
                  <input
                    type="date"
                    value={toYYYYMMDD(time1)}
                    onChange={(e) => {
                      const yyyymmdd = e.target.value;
                      if (yyyymmdd) {
                        const [yyyy, mm, dd] = yyyymmdd.split("-");
                        setTime1(`${dd}/${mm}/${yyyy}`);
                      } else setTime1("");
                    }}
                    className="date-input"
                  />
                </div>

                <div className="viz-form-group">
                  <label>End date</label>
                  <input
                    type="date"
                    value={toYYYYMMDD(time2)}
                    onChange={(e) => {
                      const yyyymmdd = e.target.value;
                      if (yyyymmdd) {
                        const [yyyy, mm, dd] = yyyymmdd.split("-");
                        setTime2(`${dd}/${mm}/${yyyy}`);
                      } else setTime2("");
                    }}
                    className="date-input"
                  />
                </div>
              </>
            )}

            <div style={{ marginTop: 18 }}>
              <VisualizationParametersPanel
                xOptions={xOptions}
                yOptions={yOptions}
                initialX="Time"
                initialY={initialY}
                onChange={handleVizParamsChange}
              />
            </div>
          </>
        )}

        {/* Taxonomy UI */}
        {domain === "Taxonomy" && (
          <div className="viz-form-group">
            <label>Select:</label>
            <select
              value={taxonomySelect}
              onChange={(e) => {
                setTaxonomySelect(e.target.value);
                setListCount("");
                setScientificName("");
                setFilterFamily("");
                setFilterGenus("");
                setFilterOrder("");
                setJsonResult(null);
                if (imageUrl) {
                  try {
                    URL.revokeObjectURL(imageUrl);
                  } catch (e) {}
                  setImageUrl(null);
                }
              }}
            >
              <option value="">-- choose action --</option>
              <option value="list">List Species</option>
              <option value="species">Species Taxonomy</option>
              <option value="filter">Filter Taxonomy</option>
            </select>

            {/* List */}
            {taxonomySelect === "list" && (
              <div style={{ marginTop: 10 }}>
                <label style={{ fontSize: 12, color: "#9aa4b2" }}>Enter number of species</label>
                <input
                  type="text"
                  placeholder="Enter number of species"
                  value={listCount}
                  onChange={(e) => setListCount(e.target.value)}
                  className="taxonomy-text-input"
                />
                <div style={{ fontSize: 12, color: "#9aa4b2", marginTop: 6 }}>/taxonomy/list</div>
              </div>
            )}

            {/* Species */}
            {taxonomySelect === "species" && (
              <div style={{ marginTop: 10 }}>
                <label style={{ fontSize: 12, color: "#9aa4b2" }}>Enter Scientific Name</label>
                <input
                  type="text"
                  placeholder="Enter Scientific Name"
                  value={scientificName}
                  onChange={(e) => setScientificName(e.target.value)}
                  className="taxonomy-text-input"
                />
                <div style={{ fontSize: 12, color: "#9aa4b2", marginTop: 6 }}>/taxonomy/species/{`{name}`}</div>
              </div>
            )}

            {/* Filter */}
            {taxonomySelect === "filter" && (
              <div style={{ marginTop: 10 }}>
                <label style={{ fontSize: 12, color: "#9aa4b2" }}>Enter Family</label>
                <input
                  type="text"
                  placeholder="Enter Family"
                  value={filterFamily}
                  onChange={(e) => setFilterFamily(e.target.value)}
                  className="taxonomy-text-input"
                />

                <label style={{ fontSize: 12, color: "#9aa4b2", marginTop: 8 }}>Enter Genus</label>
                <input
                  type="text"
                  placeholder="Enter Genus"
                  value={filterGenus}
                  onChange={(e) => setFilterGenus(e.target.value)}
                  className="taxonomy-text-input"
                />

                <label style={{ fontSize: 12, color: "#9aa4b2", marginTop: 8 }}>Enter Order</label>
                <input
                  type="text"
                  placeholder="Enter Order"
                  value={filterOrder}
                  onChange={(e) => setFilterOrder(e.target.value)}
                  className="taxonomy-text-input"
                />

                <div style={{ fontSize: 12, color: "#9aa4b2", marginTop: 6 }}>/taxonomy/filter</div>
              </div>
            )}
          </div>
        )}

        {/* Apply button & errors */}
        <button type="button" className="viz-apply-btn" onClick={handleApply} disabled={loading}>
          {loading ? "Generatingâ€¦" : "Apply Filters"}
        </button>

        {error && (
          <div className="viz-error" style={{ marginTop: 12, color: "#ffbdbd" }}>
            {error.message || "Error"}
          </div>
        )}
      </section>

      {/* LEFT: Map / Plot View */}
      <section className="viz-panel viz-panel-main">
        <div className="viz-panel-header">
          <div>
            <h3 className="viz-panel-title">Map / Plot View</h3>
            <p className="viz-panel-text">The backend response will be displayed here.</p>
          </div>
        </div>

        <div
          className="viz-map-placeholder"
          style={{ display: "flex", alignItems: "flex-start", justifyContent: "center", minHeight: 360, padding: 18 }}
        >
          {domain === "Taxonomy" ? (
            jsonResult ? (
              <div style={{ width: "100%" }}>
                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 8 }}>
                  <strong style={{ color: "#e5e7eb" }}>Taxonomy JSON Response</strong>
                  <div style={{ display: "flex", gap: 8 }}>
                    <button
                      className="viz-apply-btn"
                      style={{ padding: "6px 10px", borderRadius: 8, minWidth: 80 }}
                      onClick={() => {
                        try {
                          navigator.clipboard.writeText(JSON.stringify(jsonResult, null, 2));
                        } catch (e) {
                          console.warn("Copy failed", e);
                        }
                      }}
                    >
                      Copy JSON
                    </button>
                    <a
                      className="viz-apply-btn"
                      style={{ padding: "6px 10px", borderRadius: 8, minWidth: 80, textDecoration: "none", display: "inline-flex", alignItems: "center", justifyContent: "center" }}
                      href={`data:application/json;charset=utf-8,${encodeURIComponent(JSON.stringify(jsonResult, null, 2))}`}
                      download="taxonomy_response.json"
                    >
                      Download
                    </a>
                  </div>
                </div>

                <pre
                  className="json-viewer"
                  style={{ whiteSpace: "pre-wrap", wordBreak: "break-word" }}
                >
                  {JSON.stringify(jsonResult, null, 2)}
                </pre>
              </div>
            ) : loading ? (
              <div style={{ color: "#9fb4d8" }}>Fetching taxonomy dataâ€¦</div>
            ) : (
              <div style={{ textAlign: "center", color: "#9fb4d8" }}>
                <p style={{ marginBottom: 6 }}>Taxonomy results will appear here as formatted JSON.</p>
                <small>Choose an action in the Taxonomy panel and click "Apply Filters".</small>
              </div>
            )
          ) : imageUrl ? (
            <img
              src={imageUrl}
              alt="Generated"
              style={{ maxWidth: "100%", maxHeight: "640px", objectFit: "contain", borderRadius: 8, boxShadow: "0 6px 20px rgba(2,6,23,0.6)" }}
            />
          ) : loading ? (
            <div style={{ color: "#9fb4d8" }}>Generatingâ€¦</div>
          ) : (
            <div style={{ textAlign: "center", color: "#9fb4d8" }}>
              <p style={{ marginBottom: 6 }}>Spatial visualization / response will appear here</p>
              <small>Click "Apply Filters" to request data from the server.</small>
            </div>
          )}
        </div>
      </section>

      {/* BOTTOM: metrics (unchanged) */}
      <section className="viz-panel viz-panel-bottom">
        <h3 className="viz-panel-title">Time Series & Indicators</h3>
        <p className="viz-panel-text">Summary metrics (static placeholders)</p>

        <div className="viz-metrics-grid">
          <div className="viz-metric-card">
            <p className="viz-metric-label">SST (Â°C)</p>
            <p className="viz-metric-value">27.3</p>
            <p className="viz-metric-note">Near long-term average.</p>
          </div>
          <div className="viz-metric-card">
            <p className="viz-metric-label">Chl-a (mg/mÂ³)</p>
            <p className="viz-metric-value">0.8</p>
            <p className="viz-metric-note">Elevated coastal bloom.</p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default VisualizationTool;
